SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_vidas_simul_idade ( nr_seq_simulacao_p bigint, nr_seq_plano_preco_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint;
qt_vidas_simul_indiv_w		integer;
nr_seq_tabela_w			pls_simulpreco_individual.nr_seq_tabela%type;
ie_preco_vidas_contrato_w	pls_tabela_preco.ie_preco_vidas_contrato%type := 'N';
ie_calculo_vidas_w		pls_tabela_preco.ie_calculo_vidas%type;


BEGIN

begin
select	nr_seq_tabela
into STRICT	nr_seq_tabela_w
from	pls_simulpreco_individual
where	nr_seq_simulacao = nr_seq_simulacao_p;
exception
when others then
	nr_seq_tabela_w := null;
end;

if (nr_seq_tabela_w IS NOT NULL AND nr_seq_tabela_w::text <> '') then
	begin
	select	coalesce(ie_preco_vidas_contrato,'N'),
		coalesce(ie_calculo_vidas,'A')
	into STRICT	ie_preco_vidas_contrato_w,
		ie_calculo_vidas_w
	from	pls_tabela_preco
	where	nr_sequencia = nr_seq_tabela_w;
	exception
	when others then
		ie_preco_vidas_contrato_w	:= 'N';
		ie_calculo_vidas_w		:= 'A';
	end;

	if (ie_preco_vidas_contrato_w = 'S') then
		qt_vidas_simul_indiv_w := 0;

		if (ie_calculo_vidas_w in ('A','F')) then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual
			where	nr_seq_simulacao = nr_seq_simulacao_p;
		elsif (ie_calculo_vidas_w = 'T') then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual
			where	nr_seq_simulacao = nr_seq_simulacao_p
			and	ie_tipo_benef = 'T';
		elsif (ie_calculo_vidas_w = 'D') then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual
			where	nr_seq_simulacao = nr_seq_simulacao_p
			and	ie_tipo_benef = 'D';
		elsif (ie_calculo_vidas_w = 'TD') then
			select	count(1)
			into STRICT	qt_vidas_simul_indiv_w
			from	pls_simulpreco_individual a
			where	nr_seq_simulacao = nr_seq_simulacao_p
			and	((ie_tipo_benef = 'T') or ((ie_tipo_benef = 'D') and ((	SELECT	count(1)
											from	grau_parentesco x
											where	x.nr_sequencia = a.nr_seq_parentesco
											and	x.ie_tipo_parentesco = '1') > 0)));
		end if;
	end if;
end if;

select	count(1)
into STRICT	qt_retorno_w
from	pls_simulpreco_individual	a,
	pls_simulacao_preco      	b,
	pls_tabela_preco		c,
	pls_plano_preco			d
where	a.nr_seq_simulacao 	= b.nr_sequencia
and	a.nr_seq_tabela		= c.nr_sequencia
and	d.nr_seq_tabela		= c.nr_sequencia
and	b.nr_sequencia		= nr_seq_simulacao_p
and	d.nr_sequencia		= nr_seq_plano_preco_p
and	a.qt_idade between d.qt_idade_inicial and d.qt_idade_final
and	((ie_preco_vidas_contrato_w = 'S' and
	  qt_vidas_simul_indiv_w between coalesce(d.qt_vidas_inicial,qt_vidas_simul_indiv_w) and coalesce(d.qt_vidas_final,qt_vidas_simul_indiv_w)) or (ie_preco_vidas_contrato_w = 'N'));

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
