SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_quantidade_vidas ( nr_sequencia_p bigint, ie_opcao_p text, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

/* ie_opcao_p
	T - Todos
	I - Titular
	L - Parentes legais
	E - Titular + Parentes legais
*/


/* ie_tipo_p
	C - Contrato
	P - Proposta de adesão
	S - Simulador de preço
	PO - Proposta online
*/
ds_retorno_w			bigint;
qt_vidas_w			bigint;
qt_vidas_subcontrato_w		bigint;
nr_seq_contrato_w		bigint;
ie_tipo_proposta_w		smallint;
qt_vidas_contrato_w		bigint;
nr_seq_simulacao_w		bigint;
nr_seq_titular_contrato_w	bigint;
nr_seq_titular_w		bigint;
nr_seq_proposta_w		bigint;
nr_contrato_principal_w		bigint;


BEGIN

if (ie_tipo_p	 = 'C') then /* Contrato */
	select	nr_seq_contrato,
		nr_seq_titular
	into STRICT	nr_seq_contrato_w,
		nr_seq_titular_w
	from	pls_segurado
	where	nr_sequencia	= nr_sequencia_p;

	/* Obter o contrato principal */

	select	coalesce(nr_contrato_principal, nr_seq_contrato_w)
	into STRICT	nr_contrato_principal_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;

	if (ie_opcao_p	= 'T') then /* Todos */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_contrato	b,
			pls_segurado 	a
		where	a.nr_seq_contrato	= b.nr_sequencia
		and	((b.nr_sequencia in (nr_seq_contrato_w, nr_contrato_principal_w)) or (b.nr_contrato_principal in (nr_seq_contrato_w, nr_contrato_principal_w)))
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	coalesce(a.dt_rescisao::text, '') = '';
	elsif (ie_opcao_p	= 'I') then /* Titular */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_contrato	b,
			pls_segurado 	a
		where	a.nr_seq_contrato	= b.nr_sequencia
		and	((b.nr_sequencia in (nr_seq_contrato_w, nr_contrato_principal_w)) or (b.nr_contrato_principal in (nr_seq_contrato_w, nr_contrato_principal_w)))
		and	a.nr_sequencia		= nr_sequencia_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	coalesce(a.dt_rescisao::text, '') = ''
		and	coalesce(a.nr_seq_titular::text, '') = '';
	elsif (ie_opcao_p	= 'L') then /* Parentes legais */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_contrato	c,
			grau_parentesco b,
			pls_segurado 	a
		where	a.nr_seq_parentesco	= b.nr_sequencia
		and	a.nr_seq_contrato	= c.nr_sequencia
		and	a.nr_seq_titular	= nr_sequencia_p
		and	((c.nr_sequencia in (nr_seq_contrato_w, nr_contrato_principal_w)) or (c.nr_contrato_principal in (nr_seq_contrato_w, nr_contrato_principal_w)))
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	coalesce(a.dt_rescisao::text, '') = ''
		and	b.ie_tipo_parentesco	= '1';
	elsif (ie_opcao_p	= 'E') then /* Titular + Parentes legais */
		if (coalesce(nr_seq_titular_w,0) = 0) then
			select	count(*)
			into STRICT	qt_vidas_w
			FROM pls_contrato c, pls_segurado a
LEFT OUTER JOIN grau_parentesco b ON (a.nr_seq_parentesco = b.nr_sequencia)
WHERE a.nr_seq_contrato	= c.nr_sequencia and ((a.nr_seq_titular	= nr_sequencia_p AND b.ie_tipo_parentesco	= '1')
			or	((a.nr_sequencia	= nr_sequencia_p) and (coalesce(a.nr_seq_titular::text, '') = ''))) and ((c.nr_sequencia in (nr_seq_contrato_w, nr_contrato_principal_w)) or (c.nr_contrato_principal in (nr_seq_contrato_w, nr_contrato_principal_w))) and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and coalesce(a.dt_rescisao::text, '') = '';
		else
			select	count(*)
			into STRICT	qt_vidas_w
			FROM pls_contrato c, pls_segurado a
LEFT OUTER JOIN grau_parentesco b ON (a.nr_seq_parentesco = b.nr_sequencia)
WHERE a.nr_seq_contrato	= c.nr_sequencia and ((a.nr_seq_titular	= nr_seq_titular_w AND b.ie_tipo_parentesco	= '1')
			or	((a.nr_sequencia	= nr_seq_titular_w) and (coalesce(a.nr_seq_titular::text, '') = ''))) and ((c.nr_sequencia in (nr_seq_contrato_w, nr_contrato_principal_w)) or (c.nr_contrato_principal in (nr_seq_contrato_w, nr_contrato_principal_w))) and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and coalesce(a.dt_rescisao::text, '') = '';
		end if;
	end if;	
	ds_retorno_w	:= (coalesce(qt_vidas_subcontrato_w,0) + coalesce(qt_vidas_w,0));
	
elsif (ie_tipo_p	= 'P') then /* Proposta de adesão */
	qt_vidas_contrato_w	:= 0;
	
	select	coalesce((	select	max(x.nr_sequencia)
			from	pls_contrato x
			where	x.nr_contrato = a.nr_seq_contrato),0),
		a.ie_tipo_proposta,
		b.nr_seq_titular_contrato,
		a.nr_sequencia,
		b.nr_seq_titular
	into STRICT	nr_seq_contrato_w,
		ie_tipo_proposta_w,
		nr_seq_titular_contrato_w,
		nr_seq_proposta_w,
		nr_seq_titular_w
	from	pls_proposta_adesao 		a,
		pls_proposta_beneficiario 	b
	where	a.nr_sequencia	= b.nr_seq_proposta
	and	b.nr_sequencia	= nr_sequencia_p;
	
	if (ie_opcao_p	= 'T') then /* Todos */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_proposta_beneficiario
		where	nr_seq_proposta	= nr_seq_proposta_w
		and	coalesce(dt_cancelamento::text, '') = '';
		
		if (nr_seq_contrato_w	<> 0) and (ie_tipo_proposta_w in (2,4)) then
			select	count(*)
			into STRICT	qt_vidas_contrato_w
			from	pls_contrato a,
				pls_segurado b
			where	a.nr_sequencia	= b.nr_seq_contrato
			and	a.nr_sequencia	= nr_seq_contrato_w
			and	b.nr_sequencia	= nr_seq_titular_contrato_w
			and	coalesce(b.dt_rescisao::text, '') = ''
			and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');
		end if;
	elsif (ie_opcao_p	= 'I') then /* Titular */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_proposta_beneficiario
		where	nr_seq_proposta	= nr_seq_proposta_w
		and	nr_sequencia	= nr_sequencia_p
		and	coalesce(nr_seq_titular::text, '') = ''
		and	coalesce(dt_cancelamento::text, '') = '';
		
		if (nr_seq_contrato_w	<> 0) and (ie_tipo_proposta_w in (2,4)) then
			select	count(*)
			into STRICT	qt_vidas_contrato_w
			from	pls_contrato a,
				pls_segurado b
			where	a.nr_sequencia	= b.nr_seq_contrato
			and	a.nr_sequencia	= nr_seq_contrato_w
			and	coalesce(b.dt_rescisao::text, '') = ''
			and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
			and	coalesce(b.nr_seq_titular::text, '') = '';
		end if;
	elsif (ie_opcao_p	= 'L') then /* Parentes legais */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_proposta_beneficiario	a,
			grau_parentesco 		b
		where	a.nr_seq_proposta	= nr_seq_proposta_w
		and	a.nr_seq_parentesco	= b.nr_sequencia
		and	a.nr_sequencia		= nr_sequencia_p
		and	b.ie_tipo_parentesco	= '1'
		and	coalesce(a.dt_cancelamento::text, '') = '';
		
		if (nr_seq_contrato_w	<> 0) and (ie_tipo_proposta_w in (2,4)) then
			select	count(*)
			into STRICT	qt_vidas_contrato_w
			from	pls_contrato a,
				pls_segurado b,
				grau_parentesco c
			where	a.nr_sequencia		= b.nr_seq_contrato
			and	b.nr_seq_parentesco	= c.nr_sequencia
			and	a.nr_sequencia		= nr_seq_contrato_w
			and	coalesce(b.dt_rescisao::text, '') = ''
			and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
			and	c.ie_tipo_parentesco	= '1';
		end if;
	elsif (ie_opcao_p	= 'E') then /* Titular + Parentes legais */

		/*Inseri select abaixo pois não estava calculando certo para os casos onde na proposta tem somente os dependentes e o titular já está no contrato */

		if (coalesce(nr_seq_titular_contrato_w,0) > 0) then
			select	count(*)
			into STRICT	qt_vidas_w
			FROM pls_proposta_beneficiario a
LEFT OUTER JOIN grau_parentesco b ON (a.nr_seq_parentesco = b.nr_sequencia)
WHERE a.nr_seq_titular_contrato	= nr_seq_titular_contrato_w and b.ie_tipo_parentesco	= '1' and a.nr_seq_proposta	= nr_seq_proposta_w and coalesce(a.dt_cancelamento::text, '') = '';
		elsif (coalesce(nr_seq_titular_w,0) = 0) then
			select	count(*)
			into STRICT	qt_vidas_w
			FROM pls_proposta_beneficiario a
LEFT OUTER JOIN grau_parentesco b ON (a.nr_seq_parentesco = b.nr_sequencia)
WHERE ((a.nr_seq_titular	= nr_sequencia_p AND b.ie_tipo_parentesco	= '1')
			or	((a.nr_sequencia	= nr_sequencia_p) and (coalesce(a.nr_seq_titular::text, '') = ''))) and a.nr_seq_proposta	= nr_seq_proposta_w and coalesce(a.dt_cancelamento::text, '') = '';		
		else
			select	count(*)
			into STRICT	qt_vidas_w
			FROM pls_proposta_beneficiario a
LEFT OUTER JOIN grau_parentesco b ON (a.nr_seq_parentesco = b.nr_sequencia)
WHERE ((a.nr_seq_titular	= nr_seq_titular_w AND b.ie_tipo_parentesco	= '1')
			or	((a.nr_sequencia	= nr_seq_titular_w) and (coalesce(a.nr_seq_titular::text, '') = ''))) and a.nr_seq_proposta	= nr_seq_proposta_w and coalesce(a.dt_cancelamento::text, '') = '';
		end if;
		
		if (nr_seq_contrato_w	<> 0) and (ie_tipo_proposta_w in (2,4)) then
			select	count(*)
			into STRICT	qt_vidas_contrato_w
			FROM pls_contrato a, pls_segurado b
LEFT OUTER JOIN grau_parentesco c ON (b.nr_seq_parentesco = c.nr_sequencia)
WHERE a.nr_sequencia		= b.nr_seq_contrato  and a.nr_sequencia		= nr_seq_contrato_w and coalesce(b.dt_rescisao::text, '') = '' and (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and ((c.ie_tipo_parentesco	= '1') or (coalesce(b.nr_seq_titular::text, '') = ''));
		end if;
	end if;
	
	ds_retorno_w	:= (qt_vidas_w + qt_vidas_contrato_w);
elsif (ie_tipo_p	= 'S') then /* Simulador de preço */
	select	nr_seq_simulacao
	into STRICT	nr_seq_simulacao_w
	from	pls_simulpreco_individual
	where	nr_sequencia	= nr_sequencia_p;
	
	if (ie_opcao_p	= 'T') then /* Todos */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_simulpreco_individual
		where	nr_seq_simulacao	= nr_seq_simulacao_w;
		
	elsif (ie_opcao_p	= 'I') then /* Titular */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_simulpreco_individual
		where	nr_seq_simulacao	= nr_seq_simulacao_w
		and	ie_tipo_benef		= 'T';
		
	elsif (ie_opcao_p	= 'L') then /* Parentes legais */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_simulpreco_individual a,
			grau_parentesco b
		where	a.nr_seq_parentesco	= b.nr_sequencia
		and	a.nr_seq_simulacao	= nr_seq_simulacao_w
		and	b.ie_tipo_parentesco	= '1';
		
	elsif (ie_opcao_p	= 'E') then /* Titular + Parentes legais */
		select	count(*)
		into STRICT	qt_vidas_w
		FROM pls_simulpreco_individual a
LEFT OUTER JOIN grau_parentesco b ON (a.nr_seq_parentesco = b.nr_sequencia)
WHERE a.nr_seq_simulacao	= nr_seq_simulacao_w and ((b.ie_tipo_parentesco	= '1')
		or (a.ie_tipo_benef		= 'T'));
	end if;
	
	ds_retorno_w	:= qt_vidas_w;
elsif (ie_tipo_p	= 'PO') then /* Solicitação de proposta online*/
	select	nr_seq_prop_online
	into STRICT	nr_seq_proposta_w
	from	pls_proposta_benef_online
	where	nr_sequencia	= nr_sequencia_p;
	
	if (ie_opcao_p	= 'T') then /* Todos */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_proposta_benef_online
		where	nr_seq_prop_online	= nr_seq_proposta_w;
		
	elsif (ie_opcao_p	= 'I') then /* Titular */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_proposta_benef_online
		where	nr_seq_prop_online	= nr_seq_proposta_w
		and	ie_tipo_benef		= 'T';
		
	elsif (ie_opcao_p	= 'L') then /* Parentes legais */
		select	count(*)
		into STRICT	qt_vidas_w
		from	pls_proposta_benef_online a,
			grau_parentesco b
		where	a.nr_seq_parentesco	= b.nr_sequencia
		and	a.nr_seq_prop_online	= nr_seq_proposta_w
		and	b.ie_tipo_parentesco	= '1';
		
	elsif (ie_opcao_p	= 'E') then /* Titular + Parentes legais */
		select	count(*)
		into STRICT	qt_vidas_w
		FROM pls_proposta_benef_online a
LEFT OUTER JOIN grau_parentesco b ON (a.nr_seq_parentesco = b.nr_sequencia)
WHERE a.nr_seq_prop_online	= nr_seq_proposta_w and ((b.ie_tipo_parentesco	= '1')
		or (a.ie_tipo_benef		= 'T'));
	end if;
	
	ds_retorno_w	:= qt_vidas_w;	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_quantidade_vidas ( nr_sequencia_p bigint, ie_opcao_p text, ie_tipo_p text) FROM PUBLIC;

