CREATE OR REPLACE FUNCTION pls_obter_rata_antecip_men_seg (nr_seq_mens_segurado_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
PR - Valor Pr√≥ Rata dia
AT - Valor Antecipacao
*/
vl_retorno_w		double precision;

BEGIN

if (ie_opcao_p	= 'PR') then
	select	sum(coalesce(a.vl_pro_rata_dia,0))
	into STRICT	vl_retorno_w
	from	pls_mensalidade_seg_item	a,
		pls_mensalidade_segurado	b
	where	b.nr_sequencia			= nr_seq_mens_segurado_p
	and	a.nr_seq_mensalidade_seg	= b.nr_sequencia;
elsif (ie_opcao_p	= 'AT') then
	select	sum(coalesce(a.vl_antecipacao,0))
	into STRICT	vl_retorno_w
	from	pls_mensalidade_seg_item	a,
		pls_mensalidade_segurado	b
	where	b.nr_sequencia			= nr_seq_mens_segurado_p
	and	a.nr_seq_mensalidade_seg	= b.nr_sequencia;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
