SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_receita_peona ( dt_competencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;
vl_liq_sem_copartic_w		double precision;
vl_antecipacao_rata_w		double precision;
vl_antecipacao_rata_ant_w	double precision;
vl_carteira_w			double precision;


BEGIN

select	sum(vl_liq_sem_copartic),
	sum(vl_antecipacao_rata),
	sum(vl_antecipacao_rata_ant)
into STRICT	vl_liq_sem_copartic_w,
	vl_antecipacao_rata_w,
	vl_antecipacao_rata_ant_w
from	pls_resumo_mensalidade_peona_v
where	trunc(dt_mes_referencia,'month') = trunc(dt_competencia_p,'month');

select	coalesce(sum(b.vl_item),0)
into STRICT	vl_carteira_w
from	pls_mensalidade_seg_item	b,
	pls_mensalidade_segurado	a,
	pls_mensalidade			c,
	pls_lote_mensalidade		d
where	a.nr_sequencia			= b.nr_seq_mensalidade_seg
and	a.nr_seq_mensalidade		= c.nr_sequencia
and	c.nr_seq_lote			= d.nr_sequencia
and	b.ie_tipo_item			= '11'
and	pls_obter_se_exclui_seg_peona(a.nr_seq_segurado, a.dt_mesano_referencia, d.cd_estabelecimento) = 'N'
and	trunc(a.dt_mesano_referencia,'month') = trunc(dt_competencia_p,'month');

/* O valor do Pr√≥-Rata dia foi iniciado em 01/01/2010 */

if (trunc(dt_competencia_p,'month') < to_date('01/01/2010','dd/mm/yyyy')) then
	vl_antecipacao_rata_w		:= 0;
	vl_antecipacao_rata_ant_w	:= 0;
elsif (trunc(dt_competencia_p,'month') = to_date('01/01/2010','dd/mm/yyyy')) then
	vl_antecipacao_rata_ant_w	:= 0;
end if;

vl_retorno_w	:= vl_liq_sem_copartic_w - vl_antecipacao_rata_w - vl_carteira_w + vl_antecipacao_rata_ant_w;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_receita_peona ( dt_competencia_p timestamp) FROM PUBLIC;

