SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_rede_ref_produto ( nr_seq_plano_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
C - Código
D - Descrição
DT - Descrição do tipo de rede MIN
*/
ds_retorno_w			varchar(255);
ie_abrangencia_w		varchar(255);
ie_segmentacao_w		varchar(255);
ie_acomodacao_w			varchar(255);
ie_acomodacao_aux_w		varchar(255);
cd_rede_w			varchar(255);
ie_regulamentacao_w		varchar(255);
cd_rede_refer_ptu_w		pls_plano.cd_rede_refer_ptu%type;

C01 CURSOR FOR
	SELECT	a.cd_rede
	into STRICT	cd_rede_w
	from	ptu_rede_referenciada a
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	and	((a.ie_tipo_segmentacao	= ie_segmentacao_w and (a.ie_tipo_segmentacao IS NOT NULL AND a.ie_tipo_segmentacao::text <> '')) or (coalesce(a.ie_tipo_segmentacao::text, '') = ''))
	and	((a.ie_abrangencia	= ie_abrangencia_w and (a.ie_abrangencia IS NOT NULL AND a.ie_abrangencia::text <> '')) or (coalesce(a.ie_abrangencia::text, '') = ''))
	and	((a.ie_tipo_acomodacao	= ie_acomodacao_w and coalesce(a.ie_tipo_acomodacao,'NA') <> 'NA') or (coalesce(a.ie_tipo_acomodacao,'NA') = 'NA'))
	and	((a.ie_regulamentacao	= ie_regulamentacao_w and (a.ie_regulamentacao IS NOT NULL AND a.ie_regulamentacao::text <> '')) or (coalesce(a.ie_regulamentacao::text, '') = ''))
	and	a.ie_situacao		= 'A'
	and	((exists (SELECT	1
				from	pls_plano_rede_atend x
				where	x.nr_seq_plano = nr_seq_plano_p
				and	x.nr_seq_rede = a.nr_seq_rede)) or (coalesce(a.nr_seq_rede::text, '') = ''))
	order by CASE WHEN a.ie_abrangencia='' THEN '-1'  ELSE '1' END ,
		CASE WHEN a.ie_tipo_segmentacao='' THEN '-1'  ELSE '1' END ,
		CASE WHEN a.ie_tipo_acomodacao='' THEN '-1'  ELSE '1' END ,
		CASE WHEN a.ie_regulamentacao='' THEN '-1'  ELSE '1' END;


BEGIN

if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
	select	CASE WHEN ie_abrangencia='E' THEN 'ES' WHEN ie_abrangencia='GM' THEN 'RB' WHEN ie_abrangencia='GE' THEN 'RA' WHEN ie_abrangencia='N' THEN 'NA' WHEN ie_abrangencia='M' THEN 'MU' END ,
		ie_segmentacao,
		ie_regulamentacao,
		ie_acomodacao,
		cd_rede_refer_ptu
	into STRICT	ie_abrangencia_w,
		ie_segmentacao_w,
		ie_regulamentacao_w,
		ie_acomodacao_aux_w,
		cd_rede_refer_ptu_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_p;

	ie_acomodacao_w	:= pls_obter_dados_cart_unimed(null,nr_seq_plano_p,'DA',0);
	ie_acomodacao_w	:= substr(ie_acomodacao_w,1,1);

	if (coalesce(ie_acomodacao_w::text, '') = '') then
		select	CASE WHEN ie_acomodacao_aux_w='I' THEN 'A' WHEN ie_acomodacao_aux_w='C' THEN 'E'  ELSE 'NA' END
		into STRICT	ie_acomodacao_w
		;
	end if;

	open C01;
	loop
	fetch C01 into
		cd_rede_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	if (cd_rede_w IS NOT NULL AND cd_rede_w::text <> '') then
		if (ie_opcao_p = 'C') then
			ds_retorno_w := cd_rede_w;
		elsif (ie_opcao_p = 'D') then
			select	ds_rede
			into STRICT	ds_retorno_w
			from	ptu_rede_referenciada
			where	cd_rede	= cd_rede_w;
		end if;
	end if;

	if (cd_rede_refer_ptu_w IS NOT NULL AND cd_rede_refer_ptu_w::text <> '') then
		if (ie_opcao_p = 'DT') then
			select	substr(obter_valor_dominio(3259, ie_tipo_rede_min),1,30)
			into STRICT	ds_retorno_w
			from	ptu_rede_referenciada
			where	cd_rede	= cd_rede_refer_ptu_w;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_rede_ref_produto ( nr_seq_plano_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) FROM PUBLIC;

