CREATE OR REPLACE FUNCTION pls_obter_reducao_acescimo ( nr_seq_conta_pos_estab_p pls_conta_pos_estabelecido.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

 
nr_seq_regra_pos_estab_w	pls_conta_pos_estabelecido.nr_seq_regra_pos_estab%type;
tx_ajuste_ch_honor_w		pls_regra_preco_proc.tx_ajuste_ch_honor%type;
tx_ajuste_custo_oper_w		pls_regra_preco_proc.tx_ajuste_custo_oper%type;
tx_ajuste_filme_w		pls_regra_preco_proc.tx_ajuste_filme%type;
tx_custo_operacional_w		pls_proc_criterio_horario.tx_custo_operacional%type;
tx_ajuste_geral_w		pls_regra_preco_proc.tx_ajuste_geral%type;
nr_seq_regra_horario_w		pls_conta_pos_estabelecido.nr_seq_regra_horario%type;
ds_retorno_w			varchar(255);

BEGIN
 
select	max(nr_seq_regra_pos_estab), 
	max(nr_seq_regra_horario) 
into STRICT	nr_seq_regra_pos_estab_w, 
	nr_seq_regra_horario_w 
from	pls_conta_pos_estabelecido 
where	nr_sequencia			= nr_seq_conta_pos_estab_p;
 
select	max(tx_ajuste_custo_oper), 
	max(tx_ajuste_geral) 
into STRICT	tx_ajuste_custo_oper_w, 
	tx_ajuste_geral_w 
from	pls_regra_preco_proc 
where	nr_sequencia		= nr_seq_regra_pos_estab_w;
 
select	max(a.tx_custo_operacional) 
into STRICT	tx_custo_operacional_w 
from	pls_proc_criterio_horario	a 
where	a.nr_sequencia			= nr_seq_regra_horario_w;
 
if (coalesce(tx_ajuste_custo_oper_w,0) = 0) and (coalesce(tx_ajuste_geral_w,0) = 0) and (coalesce(tx_custo_operacional_w,0) = 0) then 
	ds_retorno_w	:= 0;
else 
	if (coalesce(tx_ajuste_custo_oper_w,0) = 0) then 
		tx_ajuste_custo_oper_w		:= 1;
	end if;
	 
	if (coalesce(tx_ajuste_geral_w,0) = 0) then 
		tx_ajuste_geral_w		:= 1;
	end if;
	 
	if (coalesce(tx_custo_operacional_w,0) = 0) then 
		tx_custo_operacional_w		:= 1;
	end if;
	 
	ds_retorno_w	:= ((tx_ajuste_custo_oper_w * tx_ajuste_geral_w) * tx_custo_operacional_w);
end if;
 
select	to_char(coalesce(ds_retorno_w,0), '999,999,999,990.99') 
into STRICT	ds_retorno_w
;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
