SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_regiao_arq_bi ( cd_municipio_ibge_p text, ie_retorno_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_retorno_p
P - Retorna a região superior
S - Retorna a sub-região
*/
nr_seq_regiao_bi_w		bigint;
nr_seq_sup_regiao_bi_w		bigint;


BEGIN

select	max(b.nr_sequencia),
	max(b.NR_SEQ_REGIAO_SUP)
into STRICT	nr_seq_regiao_bi_w,
	nr_seq_sup_regiao_bi_w
from	PLS_REGIAO_ARQ_BI		b,
	PLS_REGIAO_MUNICIP_ARQ_BI	a
where	a.NR_SEQ_REGIAO_BI		= b.nr_sequencia
and	a.CD_MUNICIPIO_IBGE		= cd_municipio_ibge_p;

if (ie_retorno_p = 'P') then
	return	nr_seq_sup_regiao_bi_w;
elsif (ie_retorno_p = 'S') then
	return	nr_seq_regiao_bi_w;
end if;

return	null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
