CREATE OR REPLACE FUNCTION pls_obter_regiao_estado ( sg_estado_p text) RETURNS varchar AS $body$
DECLARE


ds_regiao_w			varchar(255);


BEGIN

select	max(cd_codigo_ops)
into STRICT	ds_regiao_w
from	microregiao
where	sg_estado	= sg_estado_p;

return	ds_regiao_w;

end;
