CREATE OR REPLACE FUNCTION pls_obter_regiao_prestador ( nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_cgc_w			varchar(20);
cd_pessoa_fisica_w		varchar(20);
sg_estado_w			pessoa_juridica.sg_estado%type;
ds_regiao_w			varchar(255);


BEGIN 
 
select	obter_dados_pf_pj(cd_pessoa_fisica,cd_cgc, 'UF') 
into STRICT	sg_estado_w 
from	pls_prestador 
where	nr_sequencia	= nr_seq_prestador_p;
 
ds_regiao_w	:= pls_obter_regiao_estado(sg_estado_w);
 
return	ds_regiao_w;
 
end;
$body$
