CREATE OR REPLACE FUNCTION pls_obter_regra_agrup_analise ( nr_seq_conta_p pls_conta.nr_sequencia%type, ie_origem_conta_p pls_conta.ie_origem_conta%type, nm_usuario_p text, cd_estabelecimento_p text, nr_seq_regra_agrup_p INOUT pls_regra_geracao_analise.nr_sequencia%type) AS $body$
DECLARE


ie_forma_agrupamento_w 	pls_regra_geracao_analise.ie_forma_agrupamento%type;
nr_seq_regra_w			pls_regra_geracao_analise.nr_sequencia%type;

C01 CURSOR FOR
	SELECT  nr_sequencia,
			ie_forma_agrupamento
	from  pls_regra_geracao_analise
	where ((coalesce(ie_origem_protocolo::text, '') = '') or (ie_origem_conta_p = ie_origem_protocolo) )
	order by coalesce(ie_origem_protocolo,'Z') desc;

BEGIN

	ie_forma_agrupamento_w := 'A';
	for r_c01_w in c01 loop
		ie_forma_agrupamento_w := r_c01_w.ie_forma_agrupamento;
		nr_seq_regra_w			:= r_c01_w.nr_sequencia;
	end loop;

	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
		CALL pls_gravar_log_conta(nr_seq_conta_p, null, null, 'Obtida regra de agrupamento de an√°lise: '||nr_seq_regra_w, nm_usuario_p);
		nr_seq_regra_agrup_p := nr_seq_regra_w;
	end if;

return;

end;
$body$
