SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_regra_aud_interc ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w         		 bigint	:= null;
ie_preco_w             		 varchar(2);
nr_seq_segurado_w      	 pls_segurado.nr_sequencia%type;
ie_retorno_w           		 varchar(1)	:= 'N';
nr_seq_intercambio_w	pls_intercambio.nr_sequencia%type;
ie_tipo_contrato_w		pls_intercambio.ie_tipo_contrato%type;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	max(nr_seq_segurado)
	into STRICT	nr_seq_segurado_w
	from	pls_guia_plano
	where	nr_sequencia    = nr_seq_guia_p;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	max(nr_seq_segurado)
	into STRICT	nr_seq_segurado_w
	from	pls_requisicao
	where	nr_sequencia    = nr_seq_requisicao_p;
end if;

if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
	begin
		select 	b.ie_preco,
			nr_seq_intercambio
		into STRICT   	ie_preco_w,
			nr_seq_intercambio_w
		from    	pls_segurado a,
			pls_plano b
	where  	 	a.nr_seq_plano = b.nr_sequencia
	and    		a.nr_Sequencia = nr_seq_segurado_w;
	exception
	when others then
		ie_preco_w		:= null;
		nr_seq_intercambio_w	:= null;
	end;

	if (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
		begin
			select 	ie_tipo_contrato
			into STRICT	ie_tipo_contrato_w
			from	pls_intercambio
			where	nr_sequencia	= nr_seq_intercambio_w;
		exception
		when others then
			ie_tipo_contrato_w	:= null;
		end;
	end if;

	select	count(1)
	into STRICT    	qt_registros_w
	from	pls_regra_auditoria_interc
	where	ie_situacao     = 'A'
	and (coalesce(ie_preco::text, '') = '' or ie_preco = ie_preco_w)
	and (coalesce(ie_tipo_contrato::text, '') = '' or ie_tipo_contrato = ie_tipo_contrato_w)
	and		(ie_estagio_interc IS NOT NULL AND ie_estagio_interc::text <> '');

	if (qt_registros_w	> 0) then
        ie_retorno_w := 'S';
    end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
