CREATE OR REPLACE FUNCTION pls_obter_regra_bloq ( nr_seq_conta_p pls_conta.nr_sequencia%type, ie_tipo_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


/*
ie_tipo_p
C - Cancelamento
R - Reabertura
*/
ie_controle_producao_w		pls_controle_estab.ie_controle_producao%type;
dt_atendimento_ref_w		pls_conta.dt_atendimento_referencia%type;
dt_mes_competencia_w		pls_protocolo_conta.dt_mes_competencia%type;
dt_referencia_w			pls_protocolo_conta.dt_mes_competencia%type;
ie_retorno_w			varchar(1) := 'N';

C01 CURSOR(	ie_controle_producao_pc		pls_controle_estab.ie_controle_producao%type,
		cd_estabelecimento_pc		estabelecimento.cd_estabelecimento%type) FOR
	SELECT	ie_bloq_exclusao,
		ie_bloq_reabertura,
		ie_tipo_data,
		ie_data_referencia
	from	pls_cta_regra_bloq_acao
	where	clock_timestamp() between dt_inicio_vigencia and dt_fim_vigencia_ref
	and	ie_controle_producao_pc = 'N'
	
union all

	SELECT	ie_bloq_exclusao,
		ie_bloq_reabertura,
		ie_tipo_data,
		ie_data_referencia
	from	pls_cta_regra_bloq_acao
	where	clock_timestamp() between dt_inicio_vigencia and dt_fim_vigencia_ref
	and	ie_controle_producao_pc = 'S'
	and	cd_estabelecimento = cd_estabelecimento_pc;

BEGIN
select	coalesce(max(ie_controle_producao),'N')
into STRICT	ie_controle_producao_w
from 	pls_controle_estab;

select	max(b.dt_atendimento_referencia),
	max(a.dt_mes_competencia)
into STRICT	dt_atendimento_ref_w,
	dt_mes_competencia_w
from	pls_protocolo_conta a,
	pls_conta b
where	a.nr_sequencia = b.nr_seq_protocolo
and	b.nr_sequencia = nr_seq_conta_p;

for r_C01_w in C01(ie_controle_producao_w, cd_estabelecimento_p) loop

	if (r_C01_w.ie_data_referencia = 'DAC') then
		dt_referencia_w := dt_atendimento_ref_w;
	else
		dt_referencia_w := dt_mes_competencia_w;
	end if;

	if (r_C01_w.ie_tipo_data = 'AT') then
		if (dt_referencia_w between trunc(clock_timestamp(),'mm') and fim_mes(clock_timestamp())) then
			if (ie_tipo_p = 'C') then
				ie_retorno_w := r_C01_w.ie_bloq_exclusao;
			else
				ie_retorno_w := r_C01_w.ie_bloq_reabertura;
			end if;

			exit;
		end if;
	else
		if (dt_referencia_w < trunc(clock_timestamp(),'mm')) then
			if (ie_tipo_p = 'C') then
				ie_retorno_w := r_C01_w.ie_bloq_exclusao;
			else
				ie_retorno_w := r_C01_w.ie_bloq_reabertura;
			end if;

			exit;
		end if;
	end if;
end loop;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
