CREATE OR REPLACE FUNCTION pls_obter_regra_brasindice ( nr_seq_material_p bigint, dt_vigencia_p timestamp, nr_seq_prestador_p bigint, cd_estabelecimento_P bigint) RETURNS varchar AS $body$
DECLARE


ie_estrut_mat_w			varchar(1);
ie_tipo_preco_w			varchar(10);
ie_tipo_preco_regra_w		varchar(10);
nr_seq_estrut_mat_w		bigint;
nr_seq_estrut_regra_w		bigint;


C01 CURSOR FOR
	SELECT	ie_tipo_preco,
		nr_seq_estrutura_mat
	from	pls_regra_brasindice_preco
	where	cd_estabelecimento 				= cd_estabelecimento_p
	and	coalesce(nr_seq_material,nr_seq_material_p)		= nr_seq_material_p
	and	coalesce(nr_seq_prestador,nr_seq_prestador_p)	= nr_seq_prestador_p
	and	dt_vigencia_p between dt_inicio_vigencia and coalesce(dt_final_vigencia,dt_vigencia_p)
	and	ie_situacao = 'A'
	order by coalesce(nr_seq_estrutura_mat,0),
		coalesce(nr_seq_prestador,0),
		coalesce(cd_estabelecimento,0),
		coalesce(nr_seq_material,0);


BEGIN
select	max(nr_seq_estrut_mat)
into STRICT	nr_seq_estrut_mat_w
from	pls_material
where	nr_sequencia	= nr_seq_material_p;

open C01;
loop
fetch C01 into
	ie_tipo_preco_regra_w,
	nr_seq_estrut_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_estrut_mat_w		:= 'S';

	if (nr_seq_estrut_regra_w IS NOT NULL AND nr_seq_estrut_regra_w::text <> '') then
		if (pls_obter_se_mat_estrutura(nr_seq_material_p, nr_seq_estrut_regra_w) = 'N') then
			ie_estrut_mat_w	:= 'N';
		end if;
	end if;
	if (ie_estrut_mat_w = 'S') then
		ie_tipo_preco_w := ie_tipo_preco_regra_w;
	end if;
	end;
end loop;
close C01;

return	ie_tipo_preco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
