CREATE OR REPLACE FUNCTION pls_obter_regra_campo_ptu_mat ( dt_referencia_p timestamp, ie_tipo_intercambio_p pls_regra_campo_ptu_mat.ie_tipo_intercambio%type, ie_tipo_despesa_p pls_regra_campo_ptu_mat.ie_tipo_despesa_mat%type, cd_material_p text, ie_tipo_guia_p pls_regra_campo_ptu_mat.ie_tipo_guia%type, ds_campo_p pls_regra_campo_ptu_mat.ds_campo%type ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(5) := 'N';
nr_seq_regra_w		pls_regra_campo_ptu_mat.nr_sequencia%type;
dt_referencia_w		timestamp := trunc(dt_referencia_p);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_regra_campo_ptu_mat
where	dt_referencia_w between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_referencia_w)
and	((coalesce(cd_material_inicial::text, '') = '' and coalesce(cd_material_final::text, '') = '') or (cd_material_p between cd_material_inicial and cd_material_final))
and (ie_tipo_intercambio = 'A' or ie_tipo_intercambio = ie_tipo_intercambio_p)
and (coalesce(ie_tipo_despesa_mat::text, '') = '' or ie_tipo_despesa_mat = ie_tipo_despesa_p)
and (coalesce(ie_tipo_guia::text, '') = '' or ie_tipo_guia = ie_tipo_guia_p)
and	ds_campo	= ds_campo_p;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
