CREATE OR REPLACE FUNCTION pls_obter_regra_cod_mat ( nr_seq_estrutura_p bigint, nr_seq_material_p bigint, ie_tipo_despesa_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_cod_mat_w		bigint;
nr_seq_estrut_regra_w		bigint;
nr_seq_regra_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_estrutura
	from	pls_regra_geracao_cod_mat a
	where	coalesce(ie_tipo_despesa,ie_tipo_despesa_p)	= ie_tipo_despesa_p
	and	coalesce(dt_referencia_p,clock_timestamp()) between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,coalesce(dt_referencia_p,clock_timestamp()))
	order by nr_sequencia,
		nr_seq_estrutura;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_cod_mat_w,
	nr_seq_estrut_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_estrut_regra_w IS NOT NULL AND nr_seq_estrut_regra_w::text <> '') then
		if (pls_obter_se_mat_estrutura(nr_seq_material_p, nr_seq_estrut_regra_w) = 'N') then
			nr_seq_regra_cod_mat_w := null;
		end if;
	end if;
	if (nr_seq_regra_cod_mat_w IS NOT NULL AND nr_seq_regra_cod_mat_w::text <> '') then
		nr_seq_regra_w := nr_seq_regra_cod_mat_w;
	end if;
	end;
end loop;
close C01;

return	nr_seq_regra_w;

end;
