CREATE OR REPLACE FUNCTION pls_obter_regra_contrato_web ( nr_seq_contrato_p bigint, nr_seq_segurado_p bigint, ie_tipo_regra_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(5)	:= null;
nr_seq_contrato_w		bigint;
ie_tipo_estipulante_w		varchar(2);
ie_tipo_pagador_w		varchar(2);

/* 
ie_tipo_regra_p 
TM - Permite o titular visualizar todos as mensalidades do contrato 
PM - Permite somente os pagadores do contrato visualizarem as mensalidades 
 
*/
 
 
C01 CURSOR FOR 
	SELECT	coalesce(ie_visualiza_demonstrativo,'N') 
	from 	pls_regra_contrato_web 
	where (nr_seq_contrato = nr_seq_contrato_w or coalesce(nr_seq_contrato::text, '') = '') 
	and	((ie_tipo_estipulante = ie_tipo_estipulante_w) or (coalesce(ie_tipo_estipulante,'A') = 'A')) 
	and	((ie_tipo_pagador = ie_tipo_pagador_w) or (coalesce(ie_tipo_pagador,'A') = 'A')) 
	and	ie_visualiza_demonstrativo = ie_tipo_regra_p 
	and	clock_timestamp()	between dt_inicio_vigencia and coalesce(dt_fim_vigencia, clock_timestamp()) 
	order by coalesce(nr_seq_contrato,0), 
		coalesce(ie_tipo_estipulante,'A');


BEGIN 
 
if (ie_tipo_regra_p in ('TM', 'PM')) then 
	if (coalesce(nr_seq_contrato_p::text, '') = '') then 
		nr_seq_contrato_w 	:= pls_obter_dados_segurado(nr_seq_segurado_p, 'NC');
		ie_tipo_estipulante_w	:= pls_obter_dados_contrato(nr_seq_contrato_w, 'TE');
	else 
		nr_seq_contrato_w 	:= nr_seq_contrato_p;
		ie_tipo_estipulante_w	:= pls_obter_dados_contrato(nr_seq_contrato_w, 'TE');
	end if;
	 
	if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then 
		begin 
		select	CASE WHEN coalesce(a.cd_pessoa_fisica::text, '') = '' THEN  'PJ'  ELSE 'PF' END  
		into STRICT	ie_tipo_pagador_w 
		from	pls_contrato_pagador a, 
			pls_segurado b 
		where	a.nr_sequencia	= b.nr_seq_pagador 
		and	b.nr_sequencia	= nr_seq_segurado_p;
		exception 
		when others then 
			ie_tipo_pagador_w	:= 'PJ';
		end;
	else 
		ie_tipo_pagador_w	:= 'PJ';
	end if;
	 
	open C01;
	loop 
	fetch C01 into 
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;
 
return	coalesce(ds_retorno_w,'N');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
