CREATE OR REPLACE FUNCTION pls_obter_regra_despesa_aud ( nr_seq_auditoria_p bigint, ie_tipo_item_p text, ie_tipo_despesa_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_grupo_w			bigint;
ie_tipo_despesa_w		smallint;
ds_retorno_w			varchar(1)	:= 'N';
qt_registro_w			integer	:= 0;

C01 CURSOR FOR
	SELECT	ie_tipo_despesa
	from	pls_regra_grupo_despesa
	where	nr_seq_grupo = nr_seq_grupo_w
	and	ie_situacao = 'A';


BEGIN

select	nr_seq_grupo
into STRICT	nr_seq_grupo_w
from	pls_auditoria_grupo
where	nr_sequencia = coalesce(pls_obter_grupo_analise_atual(nr_seq_auditoria_p),0);

select	count(*)
into STRICT	qt_registro_w
from	pls_regra_grupo_despesa
where	nr_seq_grupo = nr_seq_grupo_w
and	ie_situacao = 'A';

if (qt_registro_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_tipo_despesa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_tipo_item_p = 'P') then
			if	(ie_tipo_despesa_w = 3 AND ie_tipo_despesa_p = 1) or
				(ie_tipo_despesa_w = 4 AND ie_tipo_despesa_p = 2) or
				(ie_tipo_despesa_w = 5 AND ie_tipo_despesa_p = 3) then
			ds_retorno_w := 'S';
			end if;
		elsif (ie_tipo_item_p = 'M') then
			if	(ie_tipo_despesa_w = 1 AND ie_tipo_despesa_p = 2) or
				(ie_tipo_despesa_w = 2 AND ie_tipo_despesa_p = 3) or
				(ie_tipo_despesa_w = 6 AND ie_tipo_despesa_p = 7) or
				(ie_tipo_despesa_w = 7 AND ie_tipo_despesa_p = 1) then
			ds_retorno_w := 'S';
			end if;
		end if;
		end;
	end loop;
	close C01;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
