SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_regra_endereco_benef ( nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

				 
/*ie_opcao_p 
EN - Endereço 
CO - Complemento 
NR - Número 
B - Bairro 
CEP - CEP 
DM - Municipio 
UF - Estado 
T - Telefone 
CEL - Celuar 
M - E-mail 
*/
 
			 
ds_retorno_w			varchar(255);
nr_seq_pagador_w		bigint;
nr_seq_contrato_w		bigint;
ie_pessoa_endereco_w		varchar(10);
qt_regras_corresp_w		bigint;
nr_seq_seg_end_corresp_w	bigint;
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
ie_endereco_boleto_w		varchar(10);
ie_endereco_w			bigint;
ie_opcao_w			varchar(10);


BEGIN 
 
select	nr_seq_pagador, 
	nr_seq_contrato 
into STRICT	nr_seq_pagador_w, 
	nr_seq_contrato_w 
from	pls_segurado 
where	nr_sequencia	= nr_seq_segurado_p;
 
ie_opcao_w	:= ie_opcao_p;
 
select	count(*) 
into STRICT	qt_regras_corresp_w 
from	pls_segurado_end_corresp 
where	nr_seq_segurado	= nr_seq_segurado_p;
 
if (qt_regras_corresp_w > 0) then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_seg_end_corresp_w 
	from	pls_segurado_end_corresp 
	where	nr_seq_segurado	= nr_seq_segurado_p;
else 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_seg_end_corresp_w 
	from	pls_segurado_end_corresp 
	where	nr_seq_contrato	= nr_seq_contrato_w;
end if;
 
if (nr_seq_seg_end_corresp_w IS NOT NULL AND nr_seq_seg_end_corresp_w::text <> '') then 
	select	ie_endereco_boleto, 
		ie_pessoa_endereco, 
		cd_pessoa_fisica, 
		cd_cgc 
	into STRICT	ie_endereco_boleto_w, 
		ie_pessoa_endereco_w, 
		cd_pessoa_fisica_w, 
		cd_cgc_w		 
	from	pls_segurado_end_corresp 
	where	nr_sequencia	= nr_seq_seg_end_corresp_w;
	 
	if (ie_endereco_boleto_w in ('PFR','PJC')) then 
		ie_endereco_w	:= 1;
	elsif (ie_endereco_boleto_w in ('PFC','PJF')) then 
		ie_endereco_w	:= 2;
	elsif (ie_endereco_boleto_w in ('PJ','PFV')) THEN 
		ie_endereco_w	:= 3;
	elsif (ie_endereco_boleto_w = 'PFP') THEN 
		ie_endereco_w	:= 4;
	elsif (ie_endereco_boleto_w = 'PFM') THEN 
		ie_endereco_w	:= 5;
	elsif (ie_endereco_boleto_w = 'PFJ') THEN 
		ie_endereco_w	:= 6;		
	end if;
	 
	if (coalesce(cd_pessoa_fisica_w::text, '') = '') and (coalesce(cd_cgc_w::text, '') = '') then 
		if (ie_pessoa_endereco_w = 'B') then 
			select	cd_pessoa_fisica 
			into STRICT	cd_pessoa_fisica_w 
			from	pls_segurado 
			where	nr_sequencia	= nr_seq_segurado_p;
		elsif (ie_pessoa_endereco_w = 'P') then 
			select	cd_pessoa_fisica, 
				cd_cgc 
			into STRICT	cd_pessoa_fisica_w, 
				cd_cgc_w 
			from	pls_contrato_pagador 
			where	nr_sequencia	= nr_seq_pagador_w;
		end if;
	end if;
	 
	if (ie_endereco_w	= 3) then 
		 
		if (ie_opcao_w = 'DM') then 
			ie_opcao_w	:= 'CI';
		end if;
		 
		ds_retorno_w		:= substr(obter_dados_pf_pj('',cd_cgc_w,ie_opcao_w),1,255);
	else 
		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
			ds_retorno_w		:= substr(OBTER_COMPL_PF(cd_pessoa_fisica_w,ie_endereco_w,ie_opcao_w),1,255);
		elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then 
		 
			if (ie_opcao_w = 'DM') then 
				ie_opcao_w	:= 'CI';
			elsif (ie_opcao_w = 'EN') then 
				ie_opcao_w	:= 'E';
			end if;
			 
			ds_retorno_w		:= substr(obter_compl_pj(cd_cgc_w,ie_endereco_w,ie_opcao_w),1,255);
		end if;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_regra_endereco_benef ( nr_seq_segurado_p bigint, ie_opcao_p text) FROM PUBLIC;

