CREATE OR REPLACE FUNCTION pls_obter_regra_ger_lote_cta ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_controle_producao_w		pls_controle_estab.ie_controle_producao%type;
ie_origem_protocolo_w		pls_protocolo_conta.ie_origem_protocolo%type;
ie_forma_agrupamento_w		pls_regra_geracao_lote_cta.ie_forma_agrupamento%type;

-- Por mais que a regra é simples, foi criado um cursor para caso a regra aumente posteriormente não será necessário alterar a estrutura
C01 CURSOR(	ie_origem_protocolo_pc		pls_protocolo_conta.ie_origem_protocolo%type,
		cd_estabelecimento_pc		estabelecimento.cd_estabelecimento%type,
		ie_controle_producao_pc		pls_controle_estab.ie_controle_producao%type) FOR
	SELECT	ie_forma_agrupamento
	from	pls_regra_geracao_lote_cta
	where (coalesce(ie_origem_protocolo::text, '') = '' or ie_origem_protocolo = ie_origem_protocolo_pc)
	and (ie_controle_producao_pc = 'N' or cd_estabelecimento = cd_estabelecimento_pc)
	order by ie_origem_protocolo;

BEGIN
ie_controle_producao_w := pls_obter_se_controle_estab('CPM');

-- Passado o sequencial do protocolo pois caso mais tarde
-- aumente o número de restrições não será necessário
-- aumentar o número de parâmetros da function, apenas
-- adicionar ao select abaixo
select	max(ie_origem_protocolo)
into STRICT	ie_origem_protocolo_w
from	pls_protocolo_conta
where	nr_sequencia = nr_seq_protocolo_p;

for r_C01_w in C01(ie_origem_protocolo_w, cd_estabelecimento_p, ie_controle_producao_w) loop

	ie_forma_agrupamento_w := r_C01_w.ie_forma_agrupamento;
	exit; -- Utilizado exit pois a primeira que encontrar deve ser a mais restritiva
end loop;

return	coalesce(ie_forma_agrupamento_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
