SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_regra_glosa_tiss ( cd_motivo_glosa_p text, ie_tipo_retorno_p text, dt_conta_p timestamp, dt_item_p timestamp) RETURNS varchar AS $body$
DECLARE


/*	IE_TIPO_RETORNO_P
	D - Tipo data	*/
ds_retorno_w			varchar(255);
ie_tipo_data_w			varchar(10);
nr_seq_motivo_glosa_w		bigint;
qt_regra_w			bigint;
ie_ocorrencia_w			pls_controle_estab.ie_ocorrencia%type := 'N';
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


BEGIN
begin
select	coalesce(nr_sequencia,0)
into STRICT	nr_seq_motivo_glosa_w
from	tiss_motivo_glosa
where	cd_motivo_tiss	= cd_motivo_glosa_p
and	coalesce(cd_convenio::text, '') = '';
exception
when others then
	nr_seq_motivo_glosa_w	:= 0;
end;

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
ie_ocorrencia_w := pls_obter_se_controle_estab('GO');

if (nr_seq_motivo_glosa_w > 0) then
	if (ie_ocorrencia_w = 'N') then
		select	count(1)
		into STRICT	qt_regra_w
		from	pls_regra_glosa_tiss
		where	nr_seq_motivo_glosa	= nr_seq_motivo_glosa_w  LIMIT 1;

		if (qt_regra_w > 0) then
			select	max(ie_tipo_data)
			into STRICT	ie_tipo_data_w
			from	pls_regra_glosa_tiss
			where	nr_seq_motivo_glosa	= nr_seq_motivo_glosa_w;

			if (ie_tipo_retorno_p = 'D') then
				if (ie_tipo_data_w	= 'D') then
					ds_retorno_w	:= dt_conta_p;
				elsif (ie_tipo_data_w	= 'I') then
					ds_retorno_w	:= dt_item_p;
				end if;
			end if;
		end if;
	else
		select	count(1)
		into STRICT	qt_regra_w
		from	pls_regra_glosa_tiss
		where	nr_seq_motivo_glosa	= nr_seq_motivo_glosa_w
		and	cd_estabelecimento	= cd_estabelecimento_w  LIMIT 1;

		if (qt_regra_w > 0) then
			select	max(ie_tipo_data)
			into STRICT	ie_tipo_data_w
			from	pls_regra_glosa_tiss
			where	nr_seq_motivo_glosa	= nr_seq_motivo_glosa_w
			and	cd_estabelecimento	= cd_estabelecimento_w;

			if (ie_tipo_retorno_p = 'D') then
				if (ie_tipo_data_w	= 'D') then
					ds_retorno_w	:= dt_conta_p;
				elsif (ie_tipo_data_w	= 'I') then
					ds_retorno_w	:= dt_item_p;
				end if;
			end if;
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_regra_glosa_tiss ( cd_motivo_glosa_p text, ie_tipo_retorno_p text, dt_conta_p timestamp, dt_item_p timestamp) FROM PUBLIC;

