CREATE OR REPLACE FUNCTION pls_obter_regra_grupo_aud ( nr_seq_auditoria_p bigint, cd_pessoa_fisica_p text, ie_tipo_item_p bigint, ie_tipo_regra_p text) RETURNS varchar AS $body$
DECLARE


/*
   E - Execução
   V - Visualização
*/
nr_seq_ordem_w			integer;
ds_retorno_w			varchar(2);
qt_liberacao_grupo_ant_w	integer := 0;
qt_visualizacao_w 		integer;
qt_grupo_auditor_w		integer;


BEGIN

select	count(*)
into STRICT	qt_grupo_auditor_w
from	pls_auditoria_grupo a,
	pls_grupo_auditor c,
	pls_membro_grupo_aud d
where	a.nr_seq_auditoria = nr_seq_auditoria_p
and	a.nr_seq_grupo = c.nr_sequencia
and	c.nr_sequencia = d.nr_seq_grupo
and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;

if (qt_grupo_auditor_w > 0) then
	if (ie_tipo_item_p = 1) then
		if (ie_tipo_regra_p = 'V') then

			select	count(*)
			into STRICT	qt_visualizacao_w
			from	pls_auditoria_grupo a,
				pls_grupo_auditor c,
				pls_membro_grupo_aud d
			where	a.nr_seq_auditoria = nr_seq_auditoria_p
			and	a.nr_seq_grupo = c.nr_sequencia
			and	c.nr_sequencia = d.nr_seq_grupo
			and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;

			if (qt_visualizacao_w = 0) then
				select	min(coalesce(a.nr_seq_ordem,0))
				into STRICT	nr_seq_ordem_w
				from	pls_auditoria_grupo a,
					pls_grupo_auditor c,
					pls_membro_grupo_aud d
				where	a.nr_seq_auditoria = nr_seq_auditoria_p
				and	a.nr_seq_grupo = c.nr_sequencia
				and	c.nr_sequencia = d.nr_seq_grupo
				and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;
			end if;

		elsif (ie_tipo_regra_p = 'E') then
			select	count(*)
			into STRICT	qt_visualizacao_w
			from	pls_auditoria_grupo a,
				pls_grupo_auditor c,
				pls_membro_grupo_aud d
			where	a.nr_seq_auditoria = nr_seq_auditoria_p
			and	a.nr_seq_grupo = c.nr_sequencia
			and	c.nr_sequencia = d.nr_seq_grupo
			and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;

			if (qt_visualizacao_w = 0) then
				select	coalesce(min(a.nr_seq_ordem),0)
				into STRICT	nr_seq_ordem_w
				from	pls_auditoria_grupo a,
					pls_grupo_auditor c,
					pls_membro_grupo_aud d
				where	a.nr_seq_auditoria = nr_seq_auditoria_p
				and	a.nr_seq_grupo = c.nr_sequencia
				and	c.nr_sequencia = d.nr_seq_grupo
				and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;
			end if;
		end if;

		if (nr_seq_ordem_w > 0) and (qt_visualizacao_w = 0) then
			select	count(nr_sequencia)
			into STRICT	qt_liberacao_grupo_ant_w
			from	pls_auditoria_grupo
			where	nr_seq_auditoria = nr_seq_auditoria_p
			and	coalesce(dt_liberacao::text, '') = ''
			and	nr_seq_ordem < nr_seq_ordem_w;
		end if;
	else
		if (ie_tipo_regra_p = 'V') then
			select	count(*)
			into STRICT	qt_visualizacao_w
			from	pls_auditoria_grupo a,
				pls_grupo_auditor c,
				pls_membro_grupo_aud d
			where	a.nr_seq_auditoria = nr_seq_auditoria_p
			and	a.nr_seq_grupo = c.nr_sequencia
			and	c.nr_sequencia = d.nr_seq_grupo
			and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;

			if (qt_visualizacao_w = 0) then
				select	min(coalesce(a.nr_seq_ordem,0))
				into STRICT	nr_seq_ordem_w
				from	pls_auditoria_grupo a,
					pls_grupo_auditor c,
					pls_membro_grupo_aud d
				where	a.nr_seq_auditoria = nr_seq_auditoria_p
				and	a.nr_seq_grupo = c.nr_sequencia
				and	c.nr_sequencia = d.nr_seq_grupo
				and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;
			end if;

		elsif (ie_tipo_regra_p = 'E') then
			select	count(*)
			into STRICT	qt_visualizacao_w
			from	pls_auditoria_grupo a,
				pls_grupo_auditor c,
				pls_membro_grupo_aud d
			where	a.nr_seq_auditoria = nr_seq_auditoria_p
			and	a.nr_seq_grupo = c.nr_sequencia
			and	c.nr_sequencia = d.nr_seq_grupo
			and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;

			if (qt_visualizacao_w = 0) then
				select	coalesce(min(a.nr_seq_ordem),0)
				into STRICT	nr_seq_ordem_w
				from	pls_auditoria_grupo a,
					pls_grupo_auditor c,
					pls_membro_grupo_aud d
				where	a.nr_seq_auditoria = nr_seq_auditoria_p
				and	a.nr_seq_grupo = c.nr_sequencia
				and	c.nr_sequencia = d.nr_seq_grupo
				and	d.cd_pessoa_fisica = cd_pessoa_fisica_p;
			end if;
		end if;

		if (nr_seq_ordem_w > 0) and (qt_visualizacao_w = 0) then
			select	count(nr_sequencia)
			into STRICT	qt_liberacao_grupo_ant_w
			from	pls_auditoria_grupo
			where	nr_seq_auditoria = nr_seq_auditoria_p
			and	coalesce(dt_liberacao::text, '') = ''
			and	nr_seq_ordem < nr_seq_ordem_w;
		end if;
	end if;

	if (qt_liberacao_grupo_ant_w > 0) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
