CREATE OR REPLACE FUNCTION pls_obter_regra_lib_ate_web ( nr_seq_segurado_p bigint, nr_seq_usuario_web_p bigint, ie_tipo_liberacao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			bigint;
qt_dias_liberacao_w		bigint;
ie_tipo_segurado_w		varchar(3);
ie_tipo_liberacao_w		varchar(2);
nr_seq_segurado_w		bigint;
nr_seq_usuario_web_w		bigint;
ie_tipo_usuario_w		varchar(1);


BEGIN
/*
select	nr_seq_segurado,
	nr_seq_usuario_web,
	ie_tipo_liberacao
into	nr_seq_segurado_w,
	nr_seq_usuario_web_w,
	ie_tipo_liberacao_w
from	pls_regra_lib_req_web
where	nr_sequencia = nr_seq_segurado_p;*/
nr_seq_segurado_w := nr_seq_segurado_p;
nr_seq_usuario_web_w := nr_seq_usuario_web_p;
ie_tipo_liberacao_w := ie_tipo_liberacao_p;

select	CASE WHEN coalesce(nr_seq_segurado_w::text, '') = '' THEN CASE WHEN coalesce(nr_seq_usuario_web_w::text, '') = '' THEN 'A'  ELSE 'P' END   ELSE 'B' END
into STRICT	ie_tipo_usuario_w
;

if (ie_tipo_usuario_w in ('A','B')) then
	begin
		select	ie_tipo_segurado
		into STRICT	ie_tipo_segurado_w
		from	pls_segurado
		where	nr_sequencia = nr_seq_segurado_w;
	exception
	when others then
		ie_tipo_segurado_w := null;
	end;
end if;

select	max(qt_dias_liberacao)
into STRICT	qt_dias_liberacao_w
from	pls_regra_lib_tempo_web
where	((coalesce(ie_tipo_liberacao::text, '') = '') or (ie_tipo_liberacao = ie_tipo_liberacao_w))
and	((ie_tipo_usuario = 'A') or (ie_tipo_usuario = ie_tipo_usuario_w))
and	((ie_tipo_usuario_w in ('B','A') and ((coalesce(ie_tipo_segurado::text, '') = '') or (ie_tipo_segurado = ie_tipo_segurado_w)))
or (ie_tipo_usuario_w = 'P'));

ds_retorno_w := coalesce(qt_dias_liberacao_w,0);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
