CREATE OR REPLACE FUNCTION pls_obter_regra_preco ( nr_seq_regra_p bigint, ie_tipo_regra_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);


BEGIN 
 
 
if (ie_tipo_regra_p = 'P') then 
	Select	substr(ds_regra, 1, 255) 
	into STRICT	ds_retorno_w 
	from	pls_regra_preco_proc 
	where	nr_sequencia = nr_seq_regra_p;
	 
elsif (ie_tipo_regra_p = 'M') then 
	Select	substr(ds_regra, 1, 255) 
	into STRICT	ds_retorno_w 
	from	pls_regra_preco_mat 
	where	nr_sequencia = nr_seq_regra_p;
	 
else 
	Select	substr(ds_regra, 1, 255) 
	into STRICT	ds_retorno_w 
	from	pls_regra_preco_servico 
	where	nr_sequencia = nr_seq_regra_p;
end if;
 
Return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
