CREATE OR REPLACE FUNCTION pls_obter_regra_recalculo_fixo ( dt_mes_competencia_p timestamp, cd_medico_executor_p text, nr_seq_prestador_exec_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w			bigint;


BEGIN

if (coalesce(cd_medico_executor_p,'X') <> 'X') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_prestador_pgto_fixo
	where	trunc(dt_mes_competencia_p) between trunc(dt_inicio_vigencia) and trunc(coalesce(dt_fim_vigencia, dt_mes_competencia_p))
	and	cd_pessoa_fisica	= cd_medico_executor_p
	and	nr_seq_prestador_pf	= nr_seq_prestador_exec_p;

	if (coalesce(nr_seq_regra_w,0) = 0) then
		select	max(nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	pls_prestador_pgto_fixo
		where	trunc(dt_mes_competencia_p) between trunc(dt_inicio_vigencia) and trunc(coalesce(dt_fim_vigencia, dt_mes_competencia_p))
		and	cd_pessoa_fisica	= cd_medico_executor_p
		and	nr_seq_prestador	= nr_seq_prestador_exec_p;

		if (coalesce(nr_seq_regra_w,0) = 0) then
			select	max(nr_sequencia)
			into STRICT	nr_seq_regra_w
			from	pls_prestador_pgto_fixo
			where	trunc(dt_mes_competencia_p) between trunc(dt_inicio_vigencia) and trunc(coalesce(dt_fim_vigencia, dt_mes_competencia_p))
			and	cd_pessoa_fisica	= cd_medico_executor_p
			and	coalesce(nr_seq_prestador::text, '') = ''
			and	coalesce(nr_seq_prestador_pf::text, '') = '';

		end if;
	end if;
end if;

if (coalesce(nr_seq_regra_w,0)	= 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_prestador_pgto_fixo
	where	nr_seq_prestador	= nr_seq_prestador_exec_p
	and 	coalesce(cd_pessoa_fisica::text, '') = ''
	and 	coalesce(nr_seq_prestador_pf::text, '') = ''
	and	trunc(dt_mes_competencia_p) between trunc(dt_inicio_vigencia) and trunc(coalesce(dt_fim_vigencia, dt_mes_competencia_p));
	if (coalesce(nr_seq_regra_w,0)	= 0) then
		select	max(nr_sequencia)
		into STRICT	nr_seq_regra_w
		from	pls_prestador_pgto_fixo
		where	nr_seq_prestador	= nr_seq_prestador_exec_p
		and 	coalesce(cd_pessoa_fisica::text, '') = ''
		and 	nr_seq_prestador_pf	= nr_seq_prestador_exec_p
		and	trunc(dt_mes_competencia_p) between trunc(dt_inicio_vigencia) and trunc(coalesce(dt_fim_vigencia, dt_mes_competencia_p));
	end if;
end if;

return	nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
