CREATE OR REPLACE FUNCTION pls_obter_regra_resc_fin ( nr_seq_solic_resc_fin_p pls_solic_rescisao_fin.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w		pls_resc_fin_mens.nr_sequencia%type;
ie_tipo_estipulante_w	varchar(2);
ie_tipo_pagador_w	varchar(2);
nr_seq_contrato_w	pls_contrato.nr_sequencia%type;
nr_seq_intercambio_w	pls_intercambio.nr_sequencia%type;


BEGIN

select	a.nr_seq_contrato,
	a.nr_seq_pagador_intercambio,
	CASE WHEN coalesce(a.cd_pessoa_fisica::text, '') = '' THEN  'PJ'  ELSE 'PF' END  ie_tipo_pagador
into STRICT	nr_seq_contrato_w,
	nr_seq_intercambio_w,
	ie_tipo_pagador_w
from	pls_contrato_pagador a,
	pls_solic_rescisao_fin b
where	a.nr_sequencia = b.nr_seq_pagador
and	b.nr_sequencia = nr_seq_solic_resc_fin_p;

if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
	select	CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN  'PJ'  ELSE 'PF' END
	into STRICT	ie_tipo_estipulante_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;
elsif (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
	select	CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN  'PJ'  ELSE 'PF' END
	into STRICT	ie_tipo_estipulante_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_intercambio_w;
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_resc_fin_mens
where	cd_estabelecimento = cd_estabelecimento_p
and	((coalesce(ie_tipo_estipulante,'A') = 'A') or (ie_tipo_estipulante = ie_tipo_estipulante_w))
and	((coalesce(ie_tipo_pagador,'A') = 'A') or (ie_tipo_pagador = ie_tipo_pagador_w));

return	nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
