CREATE OR REPLACE FUNCTION pls_obter_regra_senha_estip ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_segurado_p bigint, nr_contrato_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


cd_area_w			bigint;
cd_especialidade_w		bigint;
cd_grupo_w			bigint;
cd_procedimento_w		bigint;

ie_origem_proced_w		bigint;
ie_origem_proc_w		bigint;
ie_retorno_w			varchar(1)	:= 'N';

nr_contrato_w			bigint;
nr_seq_contrato_w		bigint;
nr_seq_tipo_pos_estab_w		bigint;
nr_seq_regra_w			bigint	:= 0;
nr_seq_intercambio_w		bigint;

vl_procedimento_w		double precision;

C01 CURSOR FOR
	SELECT	nr_seq_tipo_pos_estab
	from	pls_pos_estab_aut_previa
	where (nr_seq_contrato	= nr_seq_contrato_w
	or	nr_seq_intercambio 	= nr_seq_intercambio_w)
	and	nr_contrato_w		= coalesce(nr_contrato_p, nr_contrato_w)
	and	ie_situacao		= 'A'
	order by nr_seq_tipo_pos_estab;

C02 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced,
		coalesce(vl_procedimento,0)
	from	pls_guia_plano_proc
	where	nr_seq_guia		= nr_seq_guia_p
	
union

	SELECT	cd_procedimento,
		ie_origem_proced,
		coalesce(vl_procedimento,0)
	from	pls_requisicao_proc
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

C03 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_tipo_pos_estab_proc		a,
		pls_tipo_pos_estabelecido	b
	where	a.nr_seq_tipo_pos_estab				= b.nr_sequencia
	and	b.nr_sequencia					= nr_seq_tipo_pos_estab_w
	and	coalesce(a.cd_procedimento,cd_procedimento_w)	= cd_procedimento_w
	and	coalesce(a.ie_origem_proced,ie_origem_proc_w) 	= ie_origem_proc_w
	and	coalesce(a.cd_grupo_proc,cd_grupo_w)			= cd_grupo_w
	and	coalesce(a.cd_especialidade, cd_especialidade_w)	= cd_especialidade_w
	and	coalesce(a.cd_area_procedimento, cd_area_w) 		= cd_area_w
	and	((a.vl_liberado					< vl_procedimento_w)
	or	(a.vl_liberado	= 0 AND vl_procedimento_w	= 0))
	and	a.ie_liberado					= 'N'
	and	b.ie_situacao					= 'A'
	order by	cd_area_w,
			cd_especialidade_w,
			cd_grupo_w,
			cd_procedimento_w;


BEGIN



begin
	select	nr_seq_contrato,
		nr_seq_intercambio
	into STRICT	nr_seq_contrato_w,
		nr_seq_intercambio_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;

	if (coalesce(nr_seq_contrato_w,0) > 0) then
		select	nr_contrato
		into STRICT	nr_contrato_w
		from	pls_contrato
		where	nr_sequencia = nr_seq_contrato_w;
	elsif (coalesce(nr_seq_intercambio_w,0) > 0) then
		nr_contrato_w := 0;
	end if;
exception
when others then
	nr_contrato_w := 0;
	nr_seq_contrato_w := 0;
	nr_seq_intercambio_w := 0;
end;

open C01;
loop
fetch C01 into
	nr_seq_tipo_pos_estab_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		cd_procedimento_w,
		ie_origem_proced_w,
		vl_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		SELECT * FROM pls_obter_estrut_proc(cd_procedimento_w, ie_origem_proced_w, cd_area_w, cd_especialidade_w, cd_grupo_w, ie_origem_proc_w) INTO STRICT cd_area_w, cd_especialidade_w, cd_grupo_w, ie_origem_proc_w;
		open C03;
		loop
		fetch C03 into
			nr_seq_regra_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
		end loop;
		close C03;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

return	nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
