CREATE OR REPLACE FUNCTION pls_obter_regra_tempo (qt_tempo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (pls_obter_se_controle_estab('GA') = 'S') then
	begin
		select	ds_regra
		into STRICT	ds_retorno_w
		from	pls_atend_regra_tempo
		where	ie_situacao = 'A'
		and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento )
		and	qt_tempo_maximo = (	SELECT	min(qt_tempo_maximo)
						from	pls_atend_regra_tempo
						where	ie_situacao = 'A'
						and	qt_tempo_maximo >= qt_tempo_p
						and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento ));
	exception
	when others then
		ds_retorno_w := '';
	end;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	ds_regra
		into STRICT	ds_retorno_w
		from	pls_atend_regra_tempo
		where	ie_situacao = 'A'
		and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento )
		and	qt_tempo_maximo = (	SELECT	max(qt_tempo_maximo)
						from	pls_atend_regra_tempo
						where	ie_situacao = 'A'
						and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento ));
	end if;
else
	begin
		select	ds_regra
		into STRICT	ds_retorno_w
		from	pls_atend_regra_tempo
		where	ie_situacao = 'A'
		and	qt_tempo_maximo = (	SELECT	min(qt_tempo_maximo)
						from	pls_atend_regra_tempo
						where	ie_situacao = 'A'
						and	qt_tempo_maximo >= qt_tempo_p);
	exception
	when others then
		ds_retorno_w := '';
	end;

	if (coalesce(ds_retorno_w::text, '') = '') then
		select	ds_regra
		into STRICT	ds_retorno_w
		from	pls_atend_regra_tempo
		where	ie_situacao = 'A'
		and	qt_tempo_maximo = (	SELECT	max(qt_tempo_maximo)
						from	pls_atend_regra_tempo
						where	ie_situacao = 'A');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
