CREATE OR REPLACE FUNCTION pls_obter_regra_usuario_even ( nr_seq_seg_carteira_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10);
qt_registros_w			bigint;
ie_tipo_repasse_w		varchar(10);
nr_seq_segurado_w		bigint;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_regra_usuario_repasse
where	ie_situacao	= 'A';

/*Se não houver regra retorna S*/

if (qt_registros_w = 0) then
	return 'N';
end if;

select	count(1)
into STRICT	qt_registros_w
from	pls_segurado_carteira	b,
	pls_segurado		a
where	b.nr_seq_segurado	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_seg_carteira_p
and	a.ie_tipo_SEGURADO	= 'R';

/*Caso beneficiário esteja repassado retorna S*/

if (qt_registros_w = 0) then
	return 'N';
end if;

select	max(nr_seq_segurado)
into STRICT	nr_seq_segurado_w
from	pls_segurado_carteira
WHERE	nr_sequencia		= nr_seq_seg_carteira_p;

select	max(ie_tipo_repasse)
into STRICT	ie_tipo_repasse_w
from	pls_segurado_repasse
where	nr_seq_segurado	= nr_seq_segurado_w
and	clock_timestamp() between dt_repasse and coalesce(dt_fim_repasse,clock_timestamp())
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

select	count(1)
into STRICT	qt_registros_w
from	pls_regra_usuario_repasse
where	ie_situacao	= 'A'
and	ie_tipo_repasse	= ie_tipo_repasse_w;

/*Caso não exista regra para aquele repasse retorna s*/

if (qt_registros_w = 0) then
	return 'N';
end if;

select	max(ie_gerar_usuario_eventual)
into STRICT	ds_retorno_w
from	pls_regra_usuario_repasse
where	ie_situacao	= 'A'
and	ie_tipo_repasse	= ie_tipo_repasse_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_regra_usuario_even ( nr_seq_seg_carteira_p bigint, cd_estabelecimento_p bigint) FROM PUBLIC;

