SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_regra_validade_senha ( ie_origem_p text, nr_sequenica_orig_p bigint, dt_parametro_p timestamp, nr_seq_segurado_p bigint, ie_tipo_guia_p text) RETURNS varchar AS $body$
DECLARE


dt_retorno_w			varchar(15);
dt_parametro_w			timestamp;					

ie_tipo_segurado_w		varchar(3);
ie_preco_w			varchar(3)	:= '1';
ie_origem_solicitacao_w		varchar(3)	:= 'X';
ie_tipo_intercambio_w		varchar(3)	:= 'X';
ie_tipo_data_w			varchar(2)	:= '';
ie_origem_solic_w		varchar(2)	:= 'X';
ie_tipo_processo_w		varchar(2)	:= 'X';

nr_seq_contrato_w		bigint;
nr_seq_contrato_intercambio_w	bigint;
nr_seq_congenere_w		bigint;
ie_tipo_atendimento_w		varchar(2);

qt_dias_w			bigint;

nr_seq_plano_w			pls_plano.nr_sequencia%type;
cd_estabelecimento_w 		pls_guia_plano.cd_estabelecimento%type;


C01 CURSOR FOR
	SELECT	qt_dias,
		ie_tipo_data
	from	(
		SELECT	qt_dias,
			ie_tipo_data,
			nr_seq_contrato,
			nr_seq_contrato_int,
			nr_seq_grupo_contrato,
			nr_seq_congenere,
			ie_tipo_segurado,
			ie_preco,
			ie_tipo_guia,
			nr_seq_plano,
			nr_seq_grupo_produto
		from	pls_regra_validade_aut
		where	coalesce(ie_tipo_guia,ie_tipo_guia_p)				= ie_tipo_guia_p
		and	coalesce(ie_tipo_segurado,ie_tipo_segurado_w)			= ie_tipo_segurado_w
		and	coalesce(ie_preco,ie_preco_w)					= ie_preco_w
		and	coalesce(nr_seq_contrato,nr_seq_contrato_w)				= nr_seq_contrato_w
		and	coalesce(nr_seq_congenere,nr_seq_congenere_w)			= nr_seq_congenere_w
		and (coalesce(nr_seq_contrato_int::text, '') = ''	or nr_seq_contrato_int		= nr_seq_contrato_intercambio_w)
		and (coalesce(ie_origem_solicitacao::text, '') = ''	or ie_origem_solicitacao	= ie_origem_solicitacao_w)
		and (coalesce(nr_seq_grupo_contrato::text, '') = ''
		or	pls_se_grupo_preco_contrato(nr_seq_grupo_contrato, nr_seq_contrato_w, nr_seq_contrato_intercambio_w)	= 'S')
		and	((ie_autorizacao 	= 'S' and ie_origem_p 	= 'A')
		or (ie_requisicao		= 'S' and ie_origem_p 	= 'R'))
		and	ie_situacao 		= 'A'
		and (coalesce(ie_tipo_atendimento::text, '') = ''	or ie_tipo_atendimento = ie_tipo_atendimento_w)
		and (coalesce(nr_seq_plano::text, '') = ''	or nr_seq_plano = nr_seq_plano_w)
		and (coalesce(nr_seq_grupo_produto::text, '') = '' or exists (	select	1
									from	pls_preco_produto x
									where	x.nr_seq_plano	= nr_seq_plano_w
									and	x.nr_seq_grupo	= nr_seq_grupo_produto))
		and (cd_estabelecimento = coalesce(wheb_usuario_pck.get_cd_estabelecimento, cd_estabelecimento_w)
		and (pls_obter_se_controle_estab('RE') = 'S'))
		
union all

		select	qt_dias,
			ie_tipo_data,
			nr_seq_contrato,
			nr_seq_contrato_int,
			nr_seq_grupo_contrato,
			nr_seq_congenere,
			ie_tipo_segurado,
			ie_preco,
			ie_tipo_guia,
			nr_seq_plano,
			nr_seq_grupo_produto
		from	pls_regra_validade_aut
		where	coalesce(ie_tipo_guia,ie_tipo_guia_p)				= ie_tipo_guia_p
		and	coalesce(ie_tipo_segurado,ie_tipo_segurado_w)			= ie_tipo_segurado_w
		and	coalesce(ie_preco,ie_preco_w)					= ie_preco_w
		and	coalesce(nr_seq_contrato,nr_seq_contrato_w)				= nr_seq_contrato_w
		and	coalesce(nr_seq_congenere,nr_seq_congenere_w)			= nr_seq_congenere_w
		and (coalesce(nr_seq_contrato_int::text, '') = ''	or nr_seq_contrato_int		= nr_seq_contrato_intercambio_w)
		and (coalesce(ie_origem_solicitacao::text, '') = ''	or ie_origem_solicitacao	= ie_origem_solicitacao_w)
		and (coalesce(nr_seq_grupo_contrato::text, '') = '' 
		or	pls_se_grupo_preco_contrato(nr_seq_grupo_contrato, nr_seq_contrato_w, nr_seq_contrato_intercambio_w)	= 'S')
		and	((ie_autorizacao 	= 'S' and ie_origem_p 	= 'A')
		or (ie_requisicao		= 'S' and ie_origem_p 	= 'R'))
		and	ie_situacao 		= 'A'
		and (coalesce(ie_tipo_atendimento::text, '') = ''	or ie_tipo_atendimento = ie_tipo_atendimento_w)
		and (coalesce(nr_seq_plano::text, '') = ''	or nr_seq_plano = nr_seq_plano_w)
		and (coalesce(nr_seq_grupo_produto::text, '') = '' or exists (	select	1
									from	pls_preco_produto x
									where	x.nr_seq_plano	= nr_seq_plano_w
									and	x.nr_seq_grupo	= nr_seq_grupo_produto))
		and (pls_obter_se_controle_estab('RE') = 'N')) alias50
	order by
		coalesce(nr_seq_contrato,0),
		coalesce(nr_seq_contrato_int,0),
		coalesce(nr_seq_grupo_contrato,0),
		coalesce(nr_seq_congenere,0),
		coalesce(ie_tipo_segurado,0),
		coalesce(ie_preco,0),
		coalesce(ie_tipo_guia,0),
		coalesce(nr_seq_plano,0),
		coalesce(nr_seq_grupo_produto,0);


BEGIN


begin
	select	b.ie_tipo_segurado,
		a.ie_preco,
		coalesce(b.nr_seq_contrato,0),
		coalesce(b.nr_seq_intercambio,0),
		coalesce(b.nr_seq_congenere,0)
	into STRICT	ie_tipo_segurado_w,
		ie_preco_w,
		nr_seq_contrato_w,
		nr_seq_contrato_intercambio_w,
		nr_seq_congenere_w
	from	pls_plano	a,
		pls_segurado	b
	where	b.nr_seq_plano	= a.nr_sequencia
	and	b.nr_sequencia 	= nr_seq_segurado_p;
exception
when others then
	ie_tipo_segurado_w 		:= 'X';
	ie_preco_w 			:= 'X';
	nr_seq_contrato_w		:= 0;
	nr_seq_contrato_intercambio_w	:= 0;
	nr_seq_congenere_w		:= 0;
end;

if (ie_origem_p	= 'R') then
	begin
		select	ie_tipo_processo,
			ie_tipo_intercambio,
			ie_origem_solic,
			ie_tipo_atendimento,
			nr_seq_plano,
			cd_estabelecimento
		into STRICT	ie_tipo_processo_w,
			ie_tipo_intercambio_w,
			ie_origem_solic_w,
			ie_tipo_atendimento_w,
			nr_seq_plano_w,
			cd_estabelecimento_w
		from	pls_requisicao
		where	nr_sequencia	= nr_sequenica_orig_p;
	exception
	when others then
		ie_tipo_processo_w	:= 'X';
		ie_tipo_intercambio_w	:= 'X';
		ie_origem_solic_w	:= 'X';
	end;

	if (coalesce(ie_tipo_processo_w,'X')	= 'I') and (coalesce(ie_tipo_intercambio_w,'X')	= 'E') then
		ie_origem_solicitacao_w	:= 'AE';
	elsif (coalesce(ie_tipo_processo_w,'X')	= 'I') and (coalesce(ie_tipo_intercambio_w,'X')	= 'I') and (coalesce(ie_origem_solic_w,'X')	= 'P') then
		ie_origem_solicitacao_w	:= 'AIP';
	else
		ie_origem_solicitacao_w	:= 'AI';
	end if;
elsif (ie_origem_p	= 'A') then
	begin
		select	ie_tipo_processo,
			ie_tipo_intercambio,
			ie_origem_solic,
			ie_tipo_atend_tiss,
			nr_seq_plano,
			cd_estabelecimento
		into STRICT	ie_tipo_processo_w,
			ie_tipo_intercambio_w,
			ie_origem_solic_w,
			ie_tipo_atendimento_w,
			nr_seq_plano_w,
			cd_estabelecimento_w
		from	pls_guia_plano
		where	nr_sequencia	= nr_sequenica_orig_p;
	exception
	when others then
		ie_tipo_processo_w	:= 'X';
		ie_tipo_intercambio_w	:= 'X';
		ie_origem_solic_w	:= 'X';
	end;

	if (coalesce(ie_tipo_processo_w,'X')	= 'I') and (coalesce(ie_tipo_intercambio_w,'X')	= 'E') then
		ie_origem_solicitacao_w	:= 'AE';
	elsif (coalesce(ie_tipo_processo_w,'X')	= 'I') and (coalesce(ie_tipo_intercambio_w,'X')	= 'I') and (coalesce(ie_origem_solic_w,'X')	= 'P') then
		ie_origem_solicitacao_w	:= 'AIP';
	else
		ie_origem_solicitacao_w	:= 'AI';
	end if;
end if;

open C01;
loop
fetch C01 into	
	qt_dias_w,
	ie_tipo_data_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ie_tipo_data_w := coalesce(ie_tipo_data_w,'DS');

-- OS 1095801 - removi o " if (ie_origem_p = 'A') " para permitir o tratamento a seguir também parao processo de Requisição.
if (ie_tipo_data_w = 'DS') then
	dt_parametro_w := dt_parametro_p;
elsif (ie_tipo_data_w = 'DA') then
	dt_parametro_w := clock_timestamp();
end if;
		
dt_retorno_w := To_char(dt_parametro_w + qt_dias_w,'dd/mm/yyyy');

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_regra_validade_senha ( ie_origem_p text, nr_sequenica_orig_p bigint, dt_parametro_p timestamp, nr_seq_segurado_p bigint, ie_tipo_guia_p text) FROM PUBLIC;

