SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_reg_lib_intercambio (nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w		bigint;
ie_tipo_guia_w		varchar(1);
vl_autorizacao_w	double precision;
vl_max_permitido_w	double precision;


BEGIN

select	ie_tipo_guia,
	vl_autorizacao
into STRICT	ie_tipo_guia_w,
	vl_autorizacao_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;

if (pls_obter_se_controle_estab('RE') = 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_regra_lib_aut_origem
	where	ie_tipo_guia	= ie_tipo_guia_w
	and	ie_situacao	= 'A'
	and 	coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_regra_lib_aut_origem
	where	ie_tipo_guia	= ie_tipo_guia_w
	and	ie_situacao	= 'A';
end if;

if	((nr_seq_regra_w	<> 0) or (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '')) then
	select	vl_maximo
	into STRICT	vl_max_permitido_w
	from	pls_regra_lib_aut_origem
	where	nr_sequencia	= nr_seq_regra_w;

	if (vl_autorizacao_w	<= vl_max_permitido_w) then
		nr_seq_regra_w	:= 0;
	end if;
end if;

return	nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_reg_lib_intercambio (nr_seq_guia_p bigint) FROM PUBLIC;

