CREATE OR REPLACE FUNCTION pls_obter_reg_retorno_aud_orig ( nr_seq_req_proc_p bigint, nr_seq_param1_p bigint, nr_seq_param2_p bigint) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se existe regra para o retorno da auditoria da operadora de origem do beneficiário e manter a requisição em auditoria caso exista.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_regra_ret_w		bigint	:= 0;
nr_seq_grupo_auditor_w		bigint;
qt_registros_w			bigint;
nr_seq_req_proc_w		bigint;
nr_seq_retorno_w		bigint	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_grupo_auditor
	from	pls_retorno_aud_origem
	where	ie_situacao	= 'A';


BEGIN

begin
	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	pls_requisicao_proc
	where	nr_sequencia	= nr_seq_req_proc_p;
exception
when others then
	cd_procedimento_w	:= null;
	ie_origem_proced_w	:= null;
end;

open C01;
loop
fetch C01 into
	nr_seq_regra_ret_w,
	nr_seq_grupo_auditor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_ret_proc_aud_origem
	where	nr_seq_ret_aud_origem	= nr_seq_regra_ret_w
	and	cd_procedimento		= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w;

	if (qt_registros_w	> 0) then
		nr_seq_retorno_w	:= nr_seq_regra_ret_w;
		exit;
	end if;
	end;
end loop;
close C01;

return	nr_seq_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
