CREATE OR REPLACE FUNCTION pls_obter_requisicao_guia (nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_proc_w		bigint;
nr_seq_mat_w		bigint;
nr_seq_req_w		bigint;


BEGIN

select	nr_seq_req_proc,
	nr_seq_req_mat
into STRICT	nr_seq_proc_w,
	nr_seq_mat_w
from	pls_execucao_req_item
where	nr_seq_guia	= nr_seq_guia_p;

select	nr_seq_requisicao
into STRICT	nr_seq_req_w
from	pls_requisicao_proc
where	nr_sequencia = nr_seq_proc_w

union

SELECT	nr_seq_requisicao
from	pls_requisicao_mat
where	nr_sequencia = nr_seq_mat_w;

return	nr_seq_req_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
