SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_req_trans_intercamb ( nr_seq_transacao_p bigint, nr_seq_requisicao_p bigint, ie_tipo_transacao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_requisicao
	from	ptu_pedido_autorizacao
	where	nr_seq_execucao	= nr_seq_transacao_p
	
union

	SELECT	nr_seq_requisicao
	from	ptu_pedido_compl_aut
	where	nr_seq_execucao	= nr_seq_transacao_p
	
union

	select	nr_seq_requisicao
	from	ptu_requisicao_ordem_serv
	where	nr_seq_origem	= nr_seq_transacao_p;

C02 CURSOR FOR
	SELECT	nr_seq_execucao
	from	ptu_pedido_autorizacao
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	
union

	SELECT	nr_seq_execucao
	from	ptu_pedido_compl_aut
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	
union

	select	nr_seq_origem
	from	ptu_requisicao_ordem_serv
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

C03 CURSOR FOR
	SELECT	nr_seq_origem
	from	ptu_resposta_autorizacao
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	
union

	SELECT	nr_seq_origem
	from	ptu_resposta_auditoria
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	
union

	select	nr_seq_origem
	from	ptu_confirmacao
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

BEGIN

if (nr_seq_transacao_p IS NOT NULL AND nr_seq_transacao_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	if (ie_tipo_transacao_p	= 'P') then
		open C02;
		loop
		fetch C02 into
			nr_seq_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
		end loop;
		close C02;
	elsif (ie_tipo_transacao_p	= 'O') then
		open C03;
		loop
		fetch C03 into
			nr_seq_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
		end loop;
		close C03;
	end if;
end if;

return	coalesce(nr_seq_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
