CREATE OR REPLACE FUNCTION pls_obter_resposta_call_center ( nr_resposta_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ x ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(4000);


BEGIN

if (nr_resposta_p IS NOT NULL AND nr_resposta_p::text <> '') then
	select	ds_resposta
	into STRICT	ds_retorno_w
	from	pls_resp_pergunta_atend
	where	nr_sequencia = nr_resposta_p;
end if;

return	ds_retorno_w;

end;
$body$
