SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_resp_auditoria_web ( nr_seq_auditoria_p bigint, nr_seq_contrato_p bigint, nr_seq_contrato_int_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_grupo_analise_w		bigint;
ds_retorno_w			varchar(120);
ie_status_w			varchar(10);
qt_registros_w			bigint;

/*
ds_retorno_w :
	F = Finalizado
	A  = Analise
*/
BEGIN

select  pls_obter_grupo_analise_atual(nr_sequencia)
into STRICT    nr_seq_grupo_analise_w
from    pls_auditoria
where	nr_sequencia = nr_seq_auditoria_p;

if (coalesce(nr_seq_grupo_analise_w::text, '') = '') then
	ds_retorno_w := 'Finalizado';
else
	select  max(ie_status)
	into STRICT	ie_status_w
	from	pls_auditoria
	where	nr_sequencia = nr_seq_auditoria_p;

	if (ie_status_w = 'AJ') then
		ds_retorno_w := 'Prestador';
	else
		select  count(1)
		into STRICT	qt_registros_w
		from    pls_auditoria_grupo a,
			pls_grupo_auditor b,
			pls_membro_grupo_aud c
		where   a.nr_sequencia = nr_seq_grupo_analise_w
		and     b.nr_sequencia =  a.nr_seq_grupo
		and 	c.nr_seq_grupo =  a.nr_seq_grupo
		and	b.ie_tipo_auditoria = 3
		and (c.nr_seq_contrato = coalesce(nr_seq_contrato_p,0)
		or	 c.nr_seq_intercambio = coalesce(nr_seq_contrato_int_p,0));

		if (qt_registros_w > 0) then
			ds_retorno_w := 'AnÃ¡lise';
		else
			select  count(1)
			into STRICT	qt_registros_w
			from    pls_auditoria_grupo a,
				pls_grupo_auditor b
			where   a.nr_sequencia = nr_seq_grupo_analise_w
			and     b.nr_sequencia =  a.nr_seq_grupo;

			if (qt_registros_w > 0) then
				ds_retorno_w := 'Operadora';
			else
				ds_retorno_w := null;
			end if;

		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
