SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_restr_proc_inter ( NR_SEQ_INTERC_ACAO_p bigint, nr_seq_processo_evento_p bigint, ie_acao_p text, cd_municipio_ibge_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w			bigint;


BEGIN

/*verifica se a regra restringe pelo municipio*/

select	count(1)
into STRICT	qt_registros_w
from	PLS_PROC_INTERC_RESTR
where	NR_SEQ_INTERC_ACAO     	= NR_SEQ_INTERC_ACAO_p
and	CD_MUNICIPIO_IBGE		= cd_municipio_ibge_p;

if (qt_registros_w > 0) then
	return 'S';
else

	/*verifica se outra regra do evento que n√£o seja ela restringe pelo municipio*/

	select	count(1)
	into STRICT	qt_registros_w
	from	pls_processo_interc_acao	a
	where	a.ie_acao			= ie_acao_p
	and	a.nr_seq_processo_evento	= nr_seq_processo_evento_p
	and	a.nr_sequencia			<> NR_SEQ_INTERC_ACAO_p
	and	exists (	SELECT 1 from PLS_PROC_INTERC_RESTR x
					where	x.NR_SEQ_INTERC_ACAO     = a.nr_sequencia
					and	x.CD_MUNICIPIO_IBGE		= cd_municipio_ibge_p);

	if (qt_registros_w > 0) then
		return 'N';
	else
		/*verifica se regra tem municipio IBGE informado*/

		select	count(1)
		into STRICT	qt_registros_w
		from	PLS_PROC_INTERC_RESTR
		where	NR_SEQ_INTERC_ACAO     	= NR_SEQ_INTERC_ACAO_p
		and	(CD_MUNICIPIO_IBGE IS NOT NULL AND CD_MUNICIPIO_IBGE::text <> '');

		if (qt_registros_w > 0) then
			return 'N';
		else
			return 'S';
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
