SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_resumo_equipe_canal ( nr_seq_vendedor_p bigint, dt_mes_competencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
G - Global
PF - Pessoa Fisica
PJ - Pessoa Juridica
GP - Global pago
*/
qt_retorno_w		bigint := 0;
qt_sca_vendidos_w	bigint := 0;
ie_gerar_sca_w		varchar(1);


BEGIN

ie_gerar_sca_w		:= coalesce(obter_valor_param_usuario(1237, 60, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento),'S');

if (ie_opcao_p = 'G') then
	select	count(1)
	into STRICT	qt_retorno_w
	from	pls_segurado
	where	nr_seq_vendedor_canal		= nr_seq_vendedor_p
	and	trunc(dt_contratacao,'Month')	= dt_mes_competencia_p
	and	(nr_seq_contrato IS NOT NULL AND nr_seq_contrato::text <> '')
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if (ie_gerar_sca_w = 'S') then
		select	count(1)
		into STRICT	qt_sca_vendidos_w
		from	pls_sca_vinculo
		where	nr_seq_vendedor_canal			= nr_seq_vendedor_p
		and	trunc(dt_inicio_vigencia,'Month')	= dt_mes_competencia_p
		and	(nr_seq_segurado IS NOT NULL AND nr_seq_segurado::text <> '')
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	end if;
elsif (ie_opcao_p = 'PF') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pls_segurado	b,
		pls_contrato	a
	where	b.nr_seq_contrato			= a.nr_sequencia
	and	b.nr_seq_vendedor_canal			= nr_seq_vendedor_p
	and	coalesce(a.cd_cgc_estipulante::text, '') = ''
	and	trunc(b.dt_contratacao,'Month')		= dt_mes_competencia_p
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');

	if (ie_gerar_sca_w = 'S') then
		select	count(*)
		into STRICT	qt_sca_vendidos_w
		from	pls_segurado	b,
			pls_sca_vinculo	c,
			pls_contrato	a
		where	c.nr_seq_segurado		= b.nr_sequencia
		and	b.nr_seq_contrato		= a.nr_sequencia
		and	c.nr_seq_vendedor_canal		= nr_seq_vendedor_p
		and	coalesce(a.cd_cgc_estipulante::text, '') = ''
		and	trunc(c.dt_inicio_vigencia,'Month')	= dt_mes_competencia_p
		and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '');
	end if;
elsif (ie_opcao_p = 'PJ') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pls_segurado	b,
		pls_contrato	a
	where	b.nr_seq_contrato			= a.nr_sequencia
	and	b.nr_seq_vendedor_canal			= nr_seq_vendedor_p
	and	(a.cd_cgc_estipulante IS NOT NULL AND a.cd_cgc_estipulante::text <> '')
	and	trunc(b.dt_contratacao,'Month')		= dt_mes_competencia_p
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');

	if (ie_gerar_sca_w = 'S') then
		select	count(*)
		into STRICT	qt_sca_vendidos_w
		from	pls_segurado	b,
			pls_sca_vinculo	c,
			pls_contrato	a
		where	c.nr_seq_segurado		= b.nr_sequencia
		and	b.nr_seq_contrato		= a.nr_sequencia
		and	c.nr_seq_vendedor_canal		= nr_seq_vendedor_p
		and	(a.cd_cgc_estipulante IS NOT NULL AND a.cd_cgc_estipulante::text <> '')
		and	trunc(c.dt_inicio_vigencia,'Month')	= dt_mes_competencia_p
		and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '');
	end if;
elsif (ie_opcao_p = 'GP') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	titulo_receber			e,
		pls_mensalidade			d,
		pls_mensalidade_segurado	c,
		pls_segurado			b,
		pls_contrato			a
	where	b.nr_seq_contrato		= a.nr_sequencia
	and	c.nr_seq_segurado		= b.nr_sequencia
	and	c.nr_seq_mensalidade		= d.nr_sequencia
	and	e.nr_seq_mensalidade		= d.nr_sequencia
	and	b.nr_seq_vendedor_canal		= nr_seq_vendedor_p
	and	c.nr_parcela			= 1
	and	e.ie_situacao			= '2'
	and	trunc(b.dt_contratacao,'Month')	= dt_mes_competencia_p
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');
end if;

return	coalesce(qt_retorno_w,0) + coalesce(qt_sca_vendidos_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_resumo_equipe_canal ( nr_seq_vendedor_p bigint, dt_mes_competencia_p timestamp, ie_opcao_p text) FROM PUBLIC;

