SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_resumo_equipe_vend ( nr_seq_equipe_p bigint, dt_mes_competencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*
G - Global
PF - Pessoa Fisica
PJ - Pessoa Juridica
GP - Global pago
*/
qt_vendedores_w		bigint := 0;
qt_retorno_w		bigint := 0;
nr_seq_vendedor_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_vendedor
	from	pls_equipe_vend_vinculo
	where	nr_seq_equipe	= nr_seq_equipe_p
	and	dt_mes_competencia_p between coalesce(dt_inicio_vigencia,dt_mes_competencia_p) and coalesce(dt_fim_vigencia,dt_mes_competencia_p);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_vendedor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_opcao_p = 'G') then
		select	count(*)
		into STRICT	qt_vendedores_w
		from	pls_segurado
		where	nr_seq_vendedor_canal	= nr_seq_vendedor_w
		and	trunc(dt_contratacao,'Month')		= dt_mes_competencia_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	elsif (ie_opcao_p = 'PF') then
		select	count(*)
		into STRICT	qt_vendedores_w
		from	pls_segurado	b,
			pls_contrato	a
		where	b.nr_seq_contrato			= a.nr_sequencia
		and	b.nr_seq_vendedor_canal			= nr_seq_vendedor_w
		and	coalesce(a.cd_cgc_estipulante::text, '') = ''
		and	trunc(b.dt_contratacao,'Month')		= dt_mes_competencia_p
		and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');
	elsif (ie_opcao_p = 'PJ') then
		select	count(*)
		into STRICT	qt_vendedores_w
		from	pls_segurado	b,
			pls_contrato	a
		where	b.nr_seq_contrato			= a.nr_sequencia
		and	b.nr_seq_vendedor_canal			= nr_seq_vendedor_w
		and	(a.cd_cgc_estipulante IS NOT NULL AND a.cd_cgc_estipulante::text <> '')
		and	trunc(b.dt_contratacao,'Month')		= dt_mes_competencia_p
		and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');
	elsif (ie_opcao_p = 'GP') then
		select	count(*)
		into STRICT	qt_vendedores_w
		from	titulo_receber			e,
			pls_mensalidade			d,
			pls_mensalidade_segurado	c,
			pls_segurado			b,
			pls_contrato			a
		where	b.nr_seq_contrato			= a.nr_sequencia
		and	c.nr_seq_segurado			= b.nr_sequencia
		and	c.nr_seq_mensalidade			= d.nr_sequencia
		and	e.nr_seq_mensalidade			= d.nr_sequencia
		and	b.nr_seq_vendedor_canal			= nr_seq_vendedor_w
		and	c.nr_parcela				= 1
		and	e.ie_situacao				= '2'
		and	trunc(b.dt_contratacao,'Month')		= dt_mes_competencia_p
		and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');
	end if;

	qt_retorno_w := qt_retorno_w + qt_vendedores_w;

	end;
end loop;
close C01;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_resumo_equipe_vend ( nr_seq_equipe_p bigint, dt_mes_competencia_p timestamp, ie_opcao_p text) FROM PUBLIC;

