where	nr_seq_conta = nr_seq_conta_p;

if (qt_auditor_w > 0) then

	SELECT * FROM ptu_obter_dados_aud_analise(	nr_seq_conta_p, nr_crm_auditor_w, nm_medico_auditor_w, cd_uf_crm_w, nr_coren_auditor_w, nm_enfer_auditor_w, cd_uf_coren_w) INTO STRICT nr_crm_auditor_w, nm_medico_auditor_w, cd_uf_crm_w, nr_coren_auditor_w, nm_enfer_auditor_w, cd_uf_coren_w;

	if (nr_crm_auditor_w IS NOT NULL AND nr_crm_auditor_w::text <> '') then

		ds_retorno_w := 'MÃ©d.: ' || nm_medico_auditor_w || ' / CRM: ' ||  nr_crm_auditor_w;

	elsif (nr_coren_auditor_w IS NOT NULL AND nr_coren_auditor_w::text <> '') then

		ds_retorno_w := 'Enf.: ' || nm_enfer_auditor_w || ' / COREN: ' ||  nr_coren_auditor_w;
	end if;
end if;

