CREATE OR REPLACE FUNCTION pls_obter_rqe_especialidade ( cd_medico_p text, cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE


nr_rqe_w	varchar(20);


BEGIN

if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') then
	select	max(nr_rqe)
	into STRICT	nr_rqe_w
	from	medico_especialidade a
	where	a.cd_pessoa_fisica	= cd_medico_p
	and	a.cd_especialidade	= cd_especialidade_p;
end if;

return	nr_rqe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
