CREATE OR REPLACE FUNCTION pls_obter_rqe_medico ( cd_medico_p w_pls_guia_medico.cd_medico%type, cd_especialidade_p pls_prestador_med_espec.cd_especialidade%type) RETURNS MEDICO_ESPECIALIDADE.NR_RQE%TYPE AS $body$
DECLARE

 
ds_retorno_w	medico_especialidade.nr_rqe%type;				

BEGIN 
 
 
begin 
	select	x.nr_rqe 
	into STRICT	ds_retorno_w	 
	from	medico_especialidade x 
	where	x.cd_pessoa_fisica 	= cd_medico_p 
	and	x.cd_especialidade 	= cd_especialidade_p 
	and	(x.nr_rqe IS NOT NULL AND x.nr_rqe::text <> '');
exception 
when others then 
	ds_retorno_w := null;	
end;
 
return	ds_retorno_w;
 
end;
