CREATE OR REPLACE FUNCTION pls_obter_saldo_quota_coop ( nr_seq_cooperado_p text) RETURNS bigint AS $body$
DECLARE


vl_saldo_acumulado_w		double precision;


BEGIN

select	sum(a.vl_escrituracao)
into STRICT	vl_saldo_acumulado_w
from	pls_escrituracao_quota a
where	a.nr_seq_cooperado	= nr_seq_cooperado_p
and ((dt_geracao_titulos IS NOT NULL AND dt_geracao_titulos::text <> '') or exists (	SELECT	1
							from	pls_tipo_escrit_quota x
							where	x.nr_sequencia	= a.nr_seq_tipo_escrit_quota
							and	x.ie_tipo_escrituracao in ('AS','AC','DS')));

return	vl_saldo_acumulado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_saldo_quota_coop ( nr_seq_cooperado_p text) FROM PUBLIC;

