SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_saldo_recurso_glosa ( nr_seq_item_p pls_conta_proc.nr_sequencia%type, ie_tipo_item_p text, ie_opcao_p text, ie_considera_negados_p pls_parametros_rec_glosa.ie_consid_negado%type default null) RETURNS bigint AS $body$
DECLARE

		
/*
ie_opcao_p
R - Valor recursado
S - Saldo
*/
		
vl_recursado_w		pls_rec_glosa_proc.vl_recursado%type;
vl_recursado_proc_w	pls_rec_glosa_proc.vl_recursado%type;
vl_recursado_mat_w	pls_rec_glosa_proc.vl_recursado%type;
vl_retorno_w		pls_conta_proc.vl_glosa%type;
vl_glosa_w		pls_conta_proc.vl_glosa%type;
ie_consid_negados_w	pls_parametros_rec_glosa.ie_consid_negado%type;


BEGIN



if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	
	--Para tratar eventual chamada que nao esteja passando esse novo parametro, como

	--chamada por portal, etc.
	if (coalesce(ie_considera_negados_p::text, '') = '' ) then
		select 	coalesce(max(ie_consid_negado),'N')
		into STRICT	ie_consid_negados_w
		from	pls_parametros_rec_glosa;
	else
		ie_consid_negados_w := ie_considera_negados_p;
	end if;
	
	if (ie_tipo_item_p = 'P') then
		
		select	coalesce(sum(vl_glosa),0)
		into STRICT	vl_glosa_w
		from	pls_conta_proc
		where	nr_sequencia = nr_seq_item_p;
		
		select	sum(vl_recusado)
		into STRICT	vl_recursado_w
		from (
			SELECT	coalesce(sum(a.vl_recursado),0) vl_recusado
			from	pls_rec_glosa_proc a
			where	a.nr_seq_conta_proc = nr_seq_item_p
			and	a.ie_status = '1'
			
union all

			SELECT	coalesce(sum(a.vl_acatado),0) vl_recusado
			from	pls_rec_glosa_proc a
			where	a.nr_seq_conta_proc = nr_seq_item_p
			and	a.ie_status in ('2','3')
			
union all

			select	coalesce(sum(a.vl_recursado),0) vl_recusado
			from	pls_rec_glosa_proc a,
				pls_conta_rec_resumo_item b
			where	a.nr_seq_conta_proc = nr_seq_item_p
			and 	b.nr_seq_proc_rec = a.nr_sequencia
			and	a.ie_status = '4'
			and 	ie_consid_negados_w = 'S'
			and ( select count(1) from pls_pp_lote where b.nr_seq_pp_lote = nr_sequencia and ie_status = 'D') = 0
			and ( select count(1) from pls_lote_pagamento where b.nr_seq_lote_pgto = nr_sequencia and ie_status = 'D') = 0
			
union all

			select	coalesce(sum(a.vl_recursado),0)
			from	pls_rec_glosa_proc_imp a
			where	a.nr_seq_conta_proc = nr_seq_item_p
			and	not exists (	select	1
						from	pls_rec_glosa_proc x
						where	x.nr_seq_conta_proc_imp = a.nr_sequencia)) alias15;
						
	elsif (ie_tipo_item_p = 'M') then
	
		select	coalesce(sum(vl_glosa),0)
		into STRICT	vl_glosa_w
		from	pls_conta_mat
		where	nr_sequencia = nr_seq_item_p;
	
		select	sum(vl_recusado)
		into STRICT	vl_recursado_w
		from (
			SELECT	coalesce(sum(a.vl_recursado),0) vl_recusado
			from	pls_rec_glosa_mat a
			where	a.nr_seq_conta_mat = nr_seq_item_p
			and	a.ie_status = '1'
			
union all

			SELECT	coalesce(sum(a.vl_acatado),0) vl_recusado
			from	pls_rec_glosa_mat a				
			where	a.nr_seq_conta_mat = nr_seq_item_p			
			and	a.ie_status in ('2','3')
			
union all

			select	coalesce(sum(a.vl_recursado),0) vl_recusado
			from	pls_rec_glosa_mat a,
				pls_conta_rec_resumo_item b
			where	a.nr_seq_conta_mat = nr_seq_item_p
			and 	b.nr_seq_mat_rec = a.nr_sequencia
			and	a.ie_status = '4'
			and 	ie_consid_negados_w = 'S'
			and ( select count(1) from pls_pp_lote where b.nr_seq_pp_lote = nr_sequencia and ie_status = 'D') = 0
			and ( select count(1) from pls_lote_pagamento where b.nr_seq_lote_pgto = nr_sequencia and ie_status = 'D') = 0
			
union all

			select	coalesce(sum(a.vl_recursado),0)
			from	pls_rec_glosa_mat_imp a
			where	a.nr_seq_material = nr_seq_item_p
			and	not exists (	select	1
						from	pls_rec_glosa_mat x
						where	x.nr_seq_conta_mat_imp = a.nr_sequencia)) alias15;
	elsif (ie_tipo_item_p = 'C') then
		
		select	sum(vl_recusado)
		into STRICT	vl_recursado_proc_w
		from (
			SELECT	coalesce(sum(a.vl_recursado),0) vl_recusado
			from	pls_rec_glosa_proc a,
				pls_rec_glosa_conta b
			where	b.nr_sequencia = a.nr_seq_conta_rec
			and	b.nr_seq_conta = nr_seq_item_p
			and	a.ie_status = '1'
			
union all

			SELECT	coalesce(sum(a.vl_acatado),0) vl_recusado
			from	pls_rec_glosa_proc a,
				pls_rec_glosa_conta b
			where	b.nr_sequencia = a.nr_seq_conta_rec
			and	b.nr_seq_conta = nr_seq_item_p
			and	a.ie_status in ('2','3')
			
union all

			select	coalesce(sum(a.vl_recursado),0) vl_recusado
			from	pls_rec_glosa_proc a,
				pls_rec_glosa_conta b,
				pls_conta_rec_resumo_item c
			where	b.nr_sequencia = a.nr_seq_conta_rec
			and 	c.nr_seq_proc_rec = a.nr_sequencia
			and	b.nr_seq_conta = nr_seq_item_p
			and	a.ie_status = '4'
			and 	ie_consid_negados_w = 'S'
			and ( select count(1) from pls_pp_lote where c.nr_seq_pp_lote = nr_sequencia and ie_status = 'D') = 0
			and ( select count(1) from pls_lote_pagamento where c.nr_seq_lote_pgto = nr_sequencia and ie_status = 'D') = 0
			
union all

			select	coalesce(sum(a.vl_recursado),0)
			from	pls_rec_glosa_proc_imp a,
				pls_rec_glosa_conta_imp b
			where	b.nr_sequencia = a.nr_seq_conta_imp
			and	b.nr_seq_conta = nr_seq_item_p
			and	not exists (	select	1
						from	pls_rec_glosa_proc x
						where	x.nr_seq_conta_proc_imp = a.nr_sequencia)) alias16;
						
		select	sum(vl_recusado)
		into STRICT	vl_recursado_mat_w
		from (
			SELECT	coalesce(sum(a.vl_recursado),0) vl_recusado
			from	pls_rec_glosa_mat a,
				pls_rec_glosa_conta b
			where	b.nr_sequencia = a.nr_seq_conta_rec
			and	b.nr_seq_conta = nr_seq_item_p
			and	a.ie_status = '1'
			
union all

			SELECT	coalesce(sum(a.vl_acatado),0) vl_recusado
			from	pls_rec_glosa_mat a,
				pls_rec_glosa_conta b
			where	b.nr_sequencia = a.nr_seq_conta_rec
			and	b.nr_seq_conta = nr_seq_item_p
			and	a.ie_status in ('2','3')
			
union all

			select	coalesce(sum(a.vl_acatado),0) vl_recusado
			from	pls_rec_glosa_mat a,
				pls_rec_glosa_conta b,
				pls_conta_rec_resumo_item c
			where	b.nr_sequencia = a.nr_seq_conta_rec
			and 	c.nr_seq_mat_rec = a.nr_sequencia
			and	b.nr_seq_conta = nr_seq_item_p
			and	a.ie_status = '4'
			and 	ie_consid_negados_w = 'S'
			and ( select count(1) from pls_pp_lote where c.nr_seq_pp_lote = nr_sequencia and ie_status = 'D') = 0
			and ( select count(1) from pls_lote_pagamento where c.nr_seq_lote_pgto = nr_sequencia and ie_status = 'D') = 0
			
union all

			select	coalesce(sum(a.vl_recursado),0)
			from	pls_rec_glosa_mat_imp a,
				pls_rec_glosa_conta_imp b
			where	b.nr_sequencia = a.nr_seq_conta_imp
			and	b.nr_seq_conta = nr_seq_item_p
			and	not exists (	select	1
						from	pls_rec_glosa_mat x
						where	x.nr_seq_conta_mat_imp = a.nr_sequencia)) alias15;				
		
		vl_recursado_w := vl_recursado_mat_w + vl_recursado_proc_w;
		
		select	sum(vl_glosa)
		into STRICT	vl_glosa_w
		from (
			SELECT	coalesce(sum(a.vl_glosa),0) vl_glosa
			from	pls_conta_proc a
			where	a.nr_seq_conta = nr_seq_item_p
			
union all

			SELECT	coalesce(sum(a.vl_glosa),0) vl_glosa
			from	pls_conta_mat a
			where	a.nr_seq_conta = nr_seq_item_p) alias5;
		
	end if;
	
end if;

if (ie_opcao_p = 'R') then
	vl_retorno_w := vl_recursado_w;
elsif (ie_opcao_p = 'S') then
	vl_retorno_w := vl_glosa_w - vl_recursado_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_saldo_recurso_glosa ( nr_seq_item_p pls_conta_proc.nr_sequencia%type, ie_tipo_item_p text, ie_opcao_p text, ie_considera_negados_p pls_parametros_rec_glosa.ie_consid_negado%type default null) FROM PUBLIC;

