CREATE OR REPLACE FUNCTION pls_obter_saldo_rec_glosa_mat (nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, nr_seq_rec_glosa_mat_p pls_rec_glosa_mat.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE

 
vl_saldo_w		double precision;
vl_glosa_w		pls_conta_mat.vl_glosa%type;
vl_acatado_w		pls_rec_glosa_mat.vl_acatado%type;
			

BEGIN 
 
select	coalesce(max(vl_glosa),0) 
into STRICT	vl_glosa_w 
from	pls_conta_mat 
where	nr_sequencia		= nr_seq_conta_mat_p;
 
select	coalesce(sum(vl_acatado),0) 
into STRICT	vl_acatado_w 
from	pls_rec_glosa_mat 
where	nr_seq_conta_mat	= nr_seq_conta_mat_p 
and	nr_sequencia	<> coalesce(nr_seq_rec_glosa_mat_p, -1);
 
vl_saldo_w	:= vl_glosa_w - vl_acatado_w;
 
return	vl_saldo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
