SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_saldo_titulo_receber ( nr_titulo_p bigint, dt_referencia_p timestamp, cd_conta_contabil_p text) RETURNS bigint AS $body$
DECLARE

 
ie_situacao_w			varchar(1);
vl_titulo_w			double precision;
vl_saldo_data_w			double precision;
vl_recebido_w			double precision;
vl_alteracao_w			double precision;
vl_desdobrado_w			double precision;
vl_tributo_w			double precision;
vl_contab_w			double precision	:= 0;
dt_referencia_w			timestamp;
dt_contabil_w			timestamp;
dt_liquidacao_w			timestamp;
dt_antecipacao_baixa_w		timestamp;


BEGIN 
dt_referencia_w			:= trunc(dt_referencia_p,'dd') + 86399/86400;
 
begin 
select	vl_titulo, 
	dt_contabil, 
	ie_situacao, 
	dt_liquidacao 
into STRICT	vl_titulo_w, 
	dt_contabil_w, 
	ie_situacao_w, 
	dt_liquidacao_w 
from	titulo_receber 
where	nr_titulo		= nr_titulo_p;
exception 
when others then 
	vl_titulo_w		:= 0;
end;
 
select	coalesce(sum(CASE WHEN ie_aumenta_diminui='A' THEN  vl_alteracao  ELSE vl_alteracao * -1 END ),0) 
into STRICT	vl_alteracao_w 
from	alteracao_valor 
where	nr_titulo	= nr_titulo_p 
and	dt_alteracao	<= dt_referencia_w;
 
select	coalesce(sum(vl_tributo),0) 
into STRICT	vl_tributo_w 
from	titulo_receber_trib 
where	nr_titulo		= nr_titulo_p 
and	coalesce(ie_origem_tributo, 'C') = 'D';
	 
if (ie_situacao_w in ('3','5')) and (dt_liquidacao_w <= dt_referencia_w) then 
	vl_saldo_data_w	:= 0;
else 
	select	max(e.dt_antecipacao_baixa) 
	into STRICT	dt_antecipacao_baixa_w 
	FROM titulo_receber b
LEFT OUTER JOIN pls_mensalidade c ON (b.nr_seq_mensalidade = c.nr_sequencia)
LEFT OUTER JOIN pls_mensalidade_segurado d ON (c.nr_sequencia = d.nr_seq_mensalidade)
LEFT OUTER JOIN pls_mensalidade_seg_item e ON (d.nr_sequencia = e.nr_seq_mensalidade_seg)
WHERE b.nr_titulo		= nr_titulo_p;	
	 
	select	coalesce(sum(a.vl_contab_pro_rata),0) 
	into STRICT	vl_contab_w 
	from	titulo_rec_liq_cc	a, 
		titulo_receber_liq	b 
	where	b.nr_titulo	= nr_titulo_p 
	and	a.nr_seq_baixa	= b.nr_sequencia 
	and	b.nr_titulo	= a.nr_titulo 
	and	a.cd_conta_rec_pls	= cd_conta_contabil_p 
	and	ie_lote_pro_rata	= 'AN' 
	and	b.dt_recebimento <= dt_referencia_w 
	and	b.dt_recebimento >= trunc(dt_referencia_w,'month');
 
	select	coalesce(sum(coalesce(a.vl_recebido,0) + coalesce(a.vl_glosa,0) + coalesce(a.vl_descontos,0) + coalesce(a.vl_perdas,0) + coalesce(a.vl_nota_credito,0)),0) 
	into STRICT	vl_recebido_w 
	from 	titulo_receber_liq	a 
	where	a.nr_titulo		 = nr_titulo_p 
	and	coalesce(a.ie_lib_caixa, 'S') = 'S' 
	and	coalesce(null,a.dt_recebimento) <= dt_referencia_w;
 
	if (coalesce(dt_contabil_w,dt_referencia_w) > dt_referencia_w) then		 
		vl_saldo_data_w	:= 0;
	else	 
		vl_saldo_data_w	:= (vl_titulo_w + vl_contab_w) - vl_recebido_w;
	end if;
	 
	vl_saldo_data_w	:= vl_saldo_data_w + vl_alteracao_w - vl_tributo_w;
end if;
 
return coalesce(vl_saldo_data_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
