CREATE OR REPLACE FUNCTION pls_obter_sca_bonific_mens_seg ( nr_seq_mensalidade_seg_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
nr_seq_bonificao_w	bigint;
nm_bonificacao_w	varchar(255);
nr_seq_sca_w		bigint;
nm_sca_w		varchar(255);

/*
ie_opcao_p
	DS = Descrição SCA
	DB = Descrição Bonificação
	CS = Código SCA
	CB = Código Bonificação
*/
C01 CURSOR FOR
	SELECT	c.nr_sequencia,
		c.nm_bonificacao
	from	pls_mensalidade_seg_item	a,
		pls_bonificacao_vinculo		b,
		pls_bonificacao			c
	where	a.nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_p
	and	(a.nr_seq_bonificacao_vinculo IS NOT NULL AND a.nr_seq_bonificacao_vinculo::text <> '')
	and	a.nr_seq_bonificacao_vinculo	= b.nr_sequencia
	and	b.nr_seq_bonificacao		= c.nr_sequencia;

C02 CURSOR FOR
	SELECT	c.nr_sequencia,
		c.ds_plano
	from	pls_mensalidade_seg_item	a,
		pls_sca_vinculo			b,
		pls_plano			c
	where	a.nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_p
	and	(a.nr_seq_vinculo_sca IS NOT NULL AND a.nr_seq_vinculo_sca::text <> '')
	and	a.nr_seq_vinculo_sca		= b.nr_sequencia
	and	b.nr_seq_plano			= c.nr_sequencia;


BEGIN

if (ie_opcao_p	= 'CB') then
	open C01;
	loop
	fetch C01 into
		nr_seq_bonificao_w,
		nm_bonificacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= nr_seq_bonificao_w;
		else
			ds_retorno_w	:= ds_retorno_w	|| ',' || nr_seq_bonificao_w;
		end if;
		end;
	end loop;
	close C01;
elsif (ie_opcao_p	= 'DB') then
	open C01;
	loop
	fetch C01 into
		nr_seq_bonificao_w,
		nm_bonificacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= nm_bonificacao_w;
		else
			ds_retorno_w	:= ds_retorno_w	|| ',' || nm_bonificacao_w;
		end if;
		end;
	end loop;
	close C01;
elsif (ie_opcao_p	= 'CS') then
	open C02;
	loop
	fetch C02 into
		nr_seq_sca_w,
		nm_sca_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= nr_seq_sca_w;
		else
			ds_retorno_w	:= ds_retorno_w	|| ',' || nr_seq_sca_w;
		end if;
		end;
	end loop;
	close C02;
elsif (ie_opcao_p	= 'DS') then
	open C02;
	loop
	fetch C02 into
		nr_seq_sca_w,
		nm_sca_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= nm_sca_w;
		else
			ds_retorno_w	:= ds_retorno_w	|| ',' || nm_sca_w;
		end if;
		end;
	end loop;
	close C02;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
