CREATE OR REPLACE FUNCTION pls_obter_sca_orderm ( nr_seq_segurado_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_plano_w		varchar(250);


BEGIN

select	CASE WHEN ie_reg=ie_opcao_p THEN ds_plano  ELSE null END
into STRICT	ds_plano_w
from	(
	SELECT	b.ds_plano,
		row_number() OVER () AS ie_reg
	from	pls_sca_vinculo a,
		pls_plano b
	where	a.nr_seq_plano	= b.nr_sequencia
	and	a.nr_seq_segurado =nr_seq_segurado_p
	and	((coalesce(a.dt_fim_vigencia::text, '') = '') or (a.dt_fim_vigencia	> clock_timestamp()))) alias5
	where	ie_reg		= ie_opcao_p;

return	ds_plano_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
