SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_schema_web ( ds_versao_tiss_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter a descrição do schema do TISS
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_outorgante_w	bigint;
ds_diretorio_schema_w	varchar(255) := null;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_outorgante_w
from	pls_outorgante
where	cd_estabelecimento = cd_estabelecimento_p;

if (nr_seq_outorgante_w IS NOT NULL AND nr_seq_outorgante_w::text <> '') then
	select 	max(b.ds_diretorio_schema)
	into STRICT	ds_diretorio_schema_w
	from 	pls_outorgante_webservice a,
		pls_regra_webservice b,
		pls_versao_tiss c
	where 	b.nr_seq_out_webservice = a.nr_sequencia
	and	a.nr_seq_versao_tiss = c.nr_sequencia
	and	b.ie_situacao = 'A'
	and	c.cd_versao_tiss = ds_versao_tiss_p
	and	trunc(clock_timestamp()) between trunc(dt_inicio_vigencia) and trunc(coalesce(dt_fim_vigencia, clock_timestamp()))
	and 	a.nr_seq_operadora = nr_seq_outorgante_w;
end if;

return	ds_diretorio_schema_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_schema_web ( ds_versao_tiss_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

