CREATE OR REPLACE FUNCTION pls_obter_seg_preco_ativo ( nr_seq_segurado_p bigint, dt_referencia_p timestamp default clock_timestamp()) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;


BEGIN

select	max(z.nr_sequencia)
into STRICT	ds_retorno_w
from	pls_segurado_preco	z,
	pls_parametros		x
where 	x.cd_estabelecimento		= cd_estabelecimento
and	z.nr_seq_segurado		= nr_seq_segurado_p
and   	(z.dt_liberacao IS NOT NULL AND z.dt_liberacao::text <> '')
and	coalesce(z.ie_situacao,'A') = 'A'
and	(((trunc(dt_referencia_p,'Month')	>= trunc(z.dt_reajuste,'Month'))
and  	((z.cd_motivo_reajuste		<> 'E') or (coalesce(x.ie_mes_cobranca_reaj_reg,'P') = 'M')))
or  	(((z.cd_motivo_reajuste = 'E')
and (trunc(dt_referencia_p,'Month') >= trunc(add_months(z.dt_reajuste,1),'month')))
and (coalesce(x.ie_mes_cobranca_reaj_reg,'P') = 'P')));

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
