CREATE OR REPLACE FUNCTION pls_obter_seq_class_desc_sip ( nr_seq_classificacao_p bigint, cd_classificacao_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p	1 = Sequencia,
		2 = Código
		3 = Descrição

*/
ds_retorno_w		varchar(255);


BEGIN
if (ie_opcao_p	= 1) then
	select	nr_sequencia
	into STRICT	ds_retorno_w
	from	sip_item_assistencial
	where	cd_classificacao	= cd_classificacao_p;
elsif (ie_opcao_p	= 2) then
	select	cd_classificacao
	into STRICT	ds_retorno_w
	from	sip_item_assistencial
	where	nr_sequencia		= nr_seq_classificacao_p;
elsif (ie_opcao_p	= 3) then
	select	ds_item
	into STRICT	ds_retorno_w
	from	sip_item_assistencial
	where	((nr_sequencia		= nr_seq_classificacao_p)
	or (cd_classificacao	= cd_classificacao_p));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
